INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3cb76482-f6c7-4d25-a039-84778c02304f'::uuid, '«O silêncio gritou» ilustra uma ___.', 'prosopopeia', 'metaforas-figuras-linguagem', 'C2', '["hipérbato", "prosopopeia", "sinestesia", "metalepses"]', 'Reconheça e nomeie tropos: metáfora, metonímia, sinédoque, hipérbole, litotes. Identifique a figura adequada ao efeito no texto.', 'Identify tropes: metaphor, metonymy, synecdoche, hyperbole, litotes. Pick the figure that matches the textual effect.', 'Розпізнавайте тропи: метафора, метонімія, синекдоха, гіпербола, літота. Оберіть відповідну фігуру.', 'personificação', '0.5', '0', '2025-08-10 22:10:07.149032+01', '2025-08-10 22:10:07.149032+01');
