INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b3307950-97a7-4574-ab6b-84567d41a0fe'::uuid, 'Tu ___ abrir a janela um pouco.', 'podes', 'poder-conseguir', 'B1', '["poder", "podia", "podes", "consegues"]', 'A resposta correta é "podes". Usa-se "poder" para permissão/possibilidade e "conseguir" para capacidade/resultado alcançado. No presente, a forma deve concordar com o sujeito. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "podes". Use "poder" for permission/possibility and "conseguir" for ability/result achieved. In the present tense, the form must agree with the subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "podes". "poder" вживається для дозволу/можливості, а "conseguir" — для спроможності/результату. У теперішньому часі форма має узгоджуватися з підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'poder / conseguir', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:30.194917+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4a81859d-fb6a-4eeb-ba51-528785bbd0bb'::uuid, 'Quem me dera que ela ___ comigo nesta viagem!', 'fosse', 'preterito-imperfeito-conjuntivo-exclamativas', 'B2', '["iria", "fosse", "é", "será"]', 'Expressões exclamativas de desejo como ''quem me dera que'' exigem o imperfeito do conjuntivo: ''fosse''.', 'Exclamations of wish like ''quem me dera que'' require the imperfect subjunctive: ''fosse''.', 'У вигуках бажання на кшталт ''quem me dera que'' вживається імперфект кон''юнктива: ''fosse''.', NULL, '0.6', '0', '2025-08-10 20:28:53.053116+01', '2025-08-10 20:28:53.053116+01');
