INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('7878ab8d-32a2-4246-8abe-1f8cec6e1d1d'::uuid, 'As reuniões ___ todas as segundas-feiras.', 'são marcadas', 'voz-passiva', 'B1', '["é marcada", "foram marcadas", "são marcado", "são marcadas"]', 'A resposta correta é "são marcadas". A voz passiva analítica forma-se com "ser" + particípio passado, concordando em género e número com o sujeito paciente. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "são marcadas". Analytical passive voice is formed with "ser" + past participle, which agrees in gender and number with the patient subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "são marcadas". Аналітичний пасив утворюється за допомогою "ser" + дієприкметник минулого, який узгоджується за родом і числом із підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'marcar', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:27.827069+01');
