INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9ea11749-4922-4f10-bcb3-c25b74fdb6bc'::uuid, 'Ela ___ correr cinco quilómetros sem parar.', 'consegue', 'poder-conseguir', 'B1', '["conseguia", "pode", "consegue", "conseguir"]', 'A resposta correta é "consegue". Usa-se "poder" para permissão/possibilidade e "conseguir" para capacidade/resultado alcançado. No presente, a forma deve concordar com o sujeito. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "consegue". Use "poder" for permission/possibility and "conseguir" for ability/result achieved. In the present tense, the form must agree with the subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "consegue". "poder" вживається для дозволу/можливості, а "conseguir" — для спроможності/результату. У теперішньому часі форма має узгоджуватися з підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'poder / conseguir', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:37.44309+01');
