INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3ee5c6ce-9b65-456e-8102-fcb156040dfd'::uuid, 'Se houver atrasos, ___ o plano.', 'revemos', 'se-futuro-conjuntivo', 'B2', '["revemos", "reveremos", "reveemos", "revíssemos"]', 'Ordem natural: principal no presente indicativo após condição futura é aceitável em PT europeu: ''revemos o plano''.', 'In EP, main clause in the present indicative is acceptable after a future condition: ''revemos o plano''.', 'У європейській португальській допустима головна в індикативі після майбутньої умови: ''revemos''.', NULL, '0.6', '0', '2025-08-10 20:28:53.071357+01', '2025-08-10 20:28:53.071357+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('2e5763cf-3c05-4803-adbe-11345bfe7bae'::uuid, 'Ela ___ muito bem português.', 'fala', 'presente-indicativo-regulares', 'A1', '["falam", "falas", "falo", "fala"]', 'Com o sujeito ''ela'', usamos a terceira pessoa do singular ''fala'' no presente do indicativo.', 'With the subject ''ela'' (she), we use the third person singular ''fala'' in the present indicative.', 'З підметом ''ela'' (вона) ми використовуємо третю особу однини ''fala'' в теперішньому часі.', 'falar', '0.5', '0', '2025-07-21 15:46:17.229168+01', '2025-08-10 21:24:24.11316+01');
