INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6d00ca9a-e94d-405f-9d0c-adbb73ea9d0e'::uuid, 'Se tu me ___ amanhã, levo-te os documentos.', 'telefonares', 'se-futuro-conjuntivo', 'B2', '["telefonaras", "telefonarás", "telefonaste", "telefonares"]', 'Com ''se'' de valor futuro, usa-se o futuro do conjuntivo: ''telefonares''. Evita-se o indicativo futuro ''telefonarás'' e o pretérito ''telefonaste''.', 'With future ''if'', use the future subjunctive: ''telefonares''. Avoid the future indicative ''telefonarás'' and the preterite ''telefonaste''.', 'Зі значенням майбутньої умови вживається майбутній кон''юнктив: ''telefonares''. Уникаємо майбутнього індикатива ''telefonarás'' і минулого ''telefonaste''.', NULL, '0.6', '0', '2025-08-10 20:28:53.071357+01', '2025-08-10 20:28:53.071357+01');
