INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6d554aba-3309-4c80-8a6a-b93289b6d621'::uuid, 'Quero que vocês ___ aqui às nove.', 'venham', 'presente-conjuntivo-irregulares', 'B1', '["venham", "vêm", "viriam", "vieram"]', 'A resposta correta é "venham". Verbos irregulares no presente do conjuntivo: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "venham". Irregular verbs in the present subjunctive: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "venham". Неправильні дієслова в теперішньому кон’юнктиві: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'vir', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:36.128619+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('743f07d1-02ea-49b0-8725-c0be539e5d26'::uuid, 'Quem me dera que eles ___ a minha ideia!', 'aceitassem', 'frases-exclamativas-desejo', 'B2', '["aceitassem", "aceitarão", "aceitaram", "aceitam"]', 'Desejo irreal passado/presente: imperfeito do conjuntivo ''aceitassem''.', 'Unreal wish about past/present: imperfect subjunctive ''aceitassem''.', 'Нереальне побажання щодо минулого/теперішнього: імперфект кон''юнктива ''aceitassem''.', NULL, '0.6', '0', '2025-08-10 20:28:53.078997+01', '2025-08-10 20:28:53.078997+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e45b8572-25ca-4dda-8a95-3c95499174b5'::uuid, 'Eles disseram: "Vamos ajudar" → Eles disseram que ___ ajudar.', 'iam', 'discurso-direto-indireto', 'B1', '["vão", "foram", "irão", "iam"]', 'A resposta correta é "iam". No discurso indireto, os tempos e pronomes podem mudar: presente → pretérito imperfeito, futuro → condicional, pronomes e advérbios ajustam-se ao ponto de referência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "iam". In reported speech, tenses and pronouns may shift: present → imperfect past, future → conditional; pronouns/adverbs adjust to the new reference point. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "iam". У непрямій мові часи та займенники можуть змінюватися: теперішній → недоконаний минулий, майбутній → умовний; займенники/прислівники узгоджуються з новою точкою відліку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'ir', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('5c467fa3-500d-44e1-af09-fbc7dc7713de'::uuid, 'Desejo que vocês ___ felizes.', 'sejam', 'presente-conjuntivo-regulares', 'B1', '["seriam", "sejam", "são", "foram"]', 'A resposta correta é "sejam". O presente do conjuntivo usa-se após expressões de vontade, dúvida, necessidade, entre outras. Regularmente, -ar termina em -e/-es/-e/-emos/-em; -er/-ir em -a/-as/-a/-amos/-am. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "sejam". Present subjunctive is used after expressions of wish, doubt, necessity, etc. Regular verbs: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "sejam". Теперішній кон’юнктив уживається після виразів бажання, сумніву, необхідності тощо. Регулярні: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'ser', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:36.1307+01');
