INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('dc9892a6-2386-402d-bcda-0cefd1c0ead8'::uuid, 'O texto é claro ___ conciso.', 'e', 'pontuacao-expressiva', 'C2', '["—", ":", "e", ";"]', 'A pontuação expressiva inclui travessão, ponto e vírgula e dois-pontos para efeitos estilísticos. Escolha o sinal que melhor estrutura a informação.', 'Expressive punctuation uses dash, semicolon, and colon for stylistic effects. Pick the sign that structures information best.', 'Експресивна пунктуація: тире, крапка з комою, двокрапка. Обирайте знак відповідно до структури.', 'coordenação simples', '0.5', '0', '2025-08-10 22:10:07.155504+01', '2025-08-10 22:10:07.155504+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('f20dce19-9549-4b25-9404-649435b6ad84'::uuid, 'A resposta foi breve ___ precisa; não havia tempo.', 'e', 'pontuacao-expressiva', 'C2', '[":", "—", ",", "e"]', 'A pontuação expressiva inclui travessão, ponto e vírgula e dois-pontos para efeitos estilísticos. Escolha o sinal que melhor estrutura a informação.', 'Expressive punctuation uses dash, semicolon, and colon for stylistic effects. Pick the sign that structures information best.', 'Експресивна пунктуація: тире, крапка з комою, двокрапка. Обирайте знак відповідно до структури.', 'coordenação + ponto e vírgula', '0.5', '0', '2025-08-10 22:10:07.155817+01', '2025-08-10 22:10:07.155817+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('77827c64-4fe7-4b32-9b7e-c392883898d2'::uuid, 'De rara beleza ___ são tais versos.', 'mesmo', 'hiperbatos-inversoes', 'C2', '["muito", "mesmo", "mesmamente", "mesma"]', 'Hipérbatos e inversões alteram a ordem canónica por ênfase. Cuidado com a colocação pronominal em europeu: próclise, ênclise e mesóclise raríssima.', 'Hyperbatons invert canonical order for emphasis. Mind clitic placement in EP: proclisis/enclisis; mesoclisis is rare.', 'Гіпербатон змінює звичний порядок слів. Звертайте увагу на позицію клитиків у EP.', 'advérbio intensificador', '0.5', '0', '2025-08-10 22:10:07.156022+01', '2025-08-10 22:10:07.156022+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9158e8ec-c58a-4e03-8c9b-3903648a32f6'::uuid, 'Não há alunos que ___ a sala.', 'ocupem', 'oracoes-relativas-conjuntivo', 'B1', '["ocupam", "ocuparão", "ocuparam", "ocupem"]', 'A resposta correta é "ocupem". Usa-se o conjuntivo em orações relativas quando há ideia de procura, desejo, indefinição: ''Procuro alguém que saiba'', ''Não há quem faça''. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "ocupem". Use the subjunctive in relative clauses expressing search, desire, or non-specificity: ''Procuro alguém que saiba'', ''Não há quem faça''. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "ocupem". У відносних підрядних із пошуком/бажанням/невизначеністю використовують кон’юнктив: ''Procuro alguém que saiba'', ''Não há quem faça''. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'ocupar', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3af2f055-678f-49f8-beec-bd899260d802'::uuid, 'Duvido que tu ___ a resposta.', 'saibas', 'presente-conjuntivo-regulares', 'B1', '["saibas", "saber", "soubeste", "sabes"]', 'A resposta correta é "saibas". O presente do conjuntivo usa-se após expressões de vontade, dúvida, necessidade, entre outras. Regularmente, -ar termina em -e/-es/-e/-emos/-em; -er/-ir em -a/-as/-a/-amos/-am. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "saibas". Present subjunctive is used after expressions of wish, doubt, necessity, etc. Regular verbs: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "saibas". Теперішній кон’юнктив уживається після виразів бажання, сумніву, необхідності тощо. Регулярні: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'saber', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:27.822892+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e11f73f8-c96a-4386-bffe-2c4441564be2'::uuid, 'Trabalhámos tanto que todos ___ exaustos.', 'ficaram', 'oracoes-finais-consecutivas', 'B1', '["ficariam", "ficaram", "ficaram-se", "ficam"]', 'A resposta correta é "ficaram". Orações finais exprimem finalidade: ''para (que) + conjuntivo/infinitivo pessoal''; consecutivas exprimem resultado: ''tão/tanto/tal... que + indicativo'' em B1. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "ficaram". Final clauses express purpose: ''para (que) + subjunctive/personal infinitive''; consecutive clauses express result: ''tão/tanto/tal... que + indicative'' at B1. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "ficaram". Цільові підрядні: ''para (que) + кон’юнктив/особовий інфінітив''; результативні: ''tão/tanto/tal... que + індикатив'' на рівні B1. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'consecutiva: tanto... que', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b6864e3f-0e11-49ed-aa0a-40cc800ca690'::uuid, 'Ela ___ na praia todos os dias.', 'corre', 'presente-indicativo-regulares', 'A1', '["corre", "corres", "corro"]', 'Com ''ela'', usamos a terceira pessoa do singular dos verbos regulares.', 'With ''she'', we use the third person singular of regular verbs.', 'З займенником ''вона'' використовуємо третю особу однини правильних дієслів.', 'correr', '0.5', '0', '2025-07-25 15:50:29.243657+01', '2025-08-10 21:24:24.11973+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('d369d7a5-bc7d-45d1-b048-13eaef30517a'::uuid, 'Quando ___ promover um evento, avise-nos com antecedência.', 'quiser', 'futuro-conjuntivo-conjuncoes', 'B2', '["quer", "quereria", "queira", "quiser"]', 'Após ''quando'' referindo-se ao futuro, usa-se o futuro do conjuntivo.', 'After ''quando'' referring to the future, we use the future subjunctive.', 'Після ''quando'', що стосується майбутнього, використовуємо майбутній час суб''юнктива.', 'querer', '0.5', '57', '2025-07-26 09:05:31.230157+01', '2025-08-11 17:01:44.374168+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('8b759cb4-519d-4f2e-b3cb-a4779daa1e93'::uuid, 'Por mais que eu ___ estudar português, ainda cometo erros.', 'tenha', 'presente-futuro-conjuntivo-concessivas', 'B2', '["tenha", "tiver", "tinha", "ter"]', 'Após ''por mais que'', usamos sempre o presente do conjuntivo para expressar uma concessão no presente.', 'After ''por mais que'' (no matter how much), we always use the present subjunctive to express a present concession.', 'Після ''por mais que'' (як би не) ми завжди використовуємо теперішній час суб''юнктива для вираження поступки в теперішньому часі.', 'ter', '0.5', '0', '2025-07-26 16:16:11.013204+01', '2025-08-10 12:19:39.245277+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('663d9120-457e-4fbe-bb0d-02cd595277d9'::uuid, 'Quem quer que ___ este documento deve assiná-lo imediatamente.', 'receba', 'oracoes-relativas-conjuntivo', 'B2', '["recebesse", "receberá", "recebe", "receba"]', 'Com ''quem quer que'' usa-se sempre o conjuntivo por se referir a uma pessoa indefinida.', 'With ''quem quer que'' we always use the subjunctive as it refers to an indefinite person.', 'З ''quem quer que'' завжди використовуємо суб''юнктив, оскільки йдеться про невизначену особу.', 'receber', '0.5', '0', '2025-07-26 16:16:17.826231+01', '2025-08-10 12:19:39.24367+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('68855ba8-dca8-4693-8538-affe56463929'::uuid, 'O João disse que ___ ao Porto na próxima semana.', 'iria', 'discurso-direto-indireto', 'B1', '["for", "ia", "vai", "iria"]', 'No discurso indireto, o futuro transforma-se em condicional.', 'In reported speech, the future tense changes to conditional.', 'У непрямій мові майбутній час змінюється на умовний.', 'ir (ele/ela)', '0.5', '0', '2025-07-25 13:51:24.436146+01', '2025-08-10 12:19:31.342372+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ab200b18-57ff-4381-a99f-060835ec86f9'::uuid, '___ dia! Como está?', 'Bom', 'presente-indicativo-regulares', 'A1', '["Bom", "Boa", "Muito", "Bem"]', 'Usamos ''Bom dia'' como saudação formal durante o dia. ''Bom'' concorda em género com a palavra masculina ''dia''.', 'We use ''Bom dia'' as a formal greeting during the day. ''Bom'' agrees in gender with the masculine noun ''dia''.', 'Ми використовуємо ''Bom dia'' як формальне вітання протягом дня. ''Bom'' узгоджується в роді з іменником чоловічого роду ''dia''.', NULL, '0.5', '0', '2025-07-25 09:21:12.586936+01', '2025-08-10 21:24:24.120149+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('63b8cd3c-e682-48da-9314-2a7f103288e0'::uuid, 'Se ___ que ia chover, tinha trazido um guarda-chuva.', 'soubesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["sabia", "soubesse", "conhecesse", "saberia"]', 'Com condicionais irreais no passado, usamos o imperfeito do conjuntivo após ''se''.', 'With unreal past conditionals, we use the imperfect subjunctive after ''se''.', 'У нереальних умовних реченнях минулого часу після ''se'' вживаємо імперфект кон''юнктива.', 'saber', '0.5', '57', '2025-07-25 15:59:24.770447+01', '2025-08-11 17:01:47.660526+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('d7f6b0be-b523-4782-a059-98f3bfd6fdad'::uuid, 'Se nós ___ mais cedo, evitávamos o trânsito.', 'saíssemos', 'se-preterito-imperfeito-conjuntivo', 'B2', '["sairíamos", "sairemos", "saíssemos", "saímos"]', 'Condição hipotética não realizada: imperfeito do conjuntivo ''saíssemos''.', 'Unreal hypothetical condition: imperfect subjunctive ''saíssemos''.', 'Нереальна гіпотетична умова: імперфект кон''юнктива ''saíssemos''.', NULL, '0.6', '57', '2025-08-10 20:28:53.061343+01', '2025-08-11 16:59:42.820633+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('a7fffa77-9be3-4540-81b7-998005f9411b'::uuid, 'Ontem eu ___ muito cedo.', 'acordei', 'preterito-perfeito-simples', 'A1', '["acordo", "acordei", "acordava"]', 'O pretérito perfeito simples usa-se para ações concluídas no passado.', 'The simple past is used for completed actions in the past.', 'Простий минулий час використовується для завершених дій у минулому.', 'acordar', '0.5', '0', '2025-07-25 15:50:27.216075+01', '2025-08-10 12:45:45.202222+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('753ec355-c67d-46f0-be50-e3ecc0da8fd5'::uuid, 'Só então ___ vimos chegar.', 'o', 'hiperbatos-inversoes', 'C2', '["o", "se", "nos", "lhe"]', 'Hipérbatos e inversões alteram a ordem canónica por ênfase. Cuidado com a colocação pronominal em europeu: próclise, ênclise e mesóclise raríssima.', 'Hyperbatons invert canonical order for emphasis. Mind clitic placement in EP: proclisis/enclisis; mesoclisis is rare.', 'Гіпербатон змінює звичний порядок слів. Звертайте увагу на позицію клитиків у EP.', 'pronome átono (próclise após adv.)', '0.5', '0', '2025-08-10 22:10:07.15625+01', '2025-08-10 22:10:07.15625+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('a547cd50-882d-491f-ada6-c8252f2ad54a'::uuid, 'Dir-te-ei a verdade, mas agora, ___, cala-te.', 'por ora', 'hiperbatos-inversoes', 'C2', '["de ora", "de já", "por ora", "para ora"]', 'Hipérbatos e inversões alteram a ordem canónica por ênfase. Cuidado com a colocação pronominal em europeu: próclise, ênclise e mesóclise raríssima.', 'Hyperbatons invert canonical order for emphasis. Mind clitic placement in EP: proclisis/enclisis; mesoclisis is rare.', 'Гіпербатон змінює звичний порядок слів. Звертайте увагу на позицію клитиків у EP.', 'locução adverbial', '0.5', '0', '2025-08-10 22:10:07.156463+01', '2025-08-10 22:10:07.156463+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ef8802e6-e6f7-4440-9d68-fe7c42b8a023'::uuid, 'Não ___ ir à festa porque estou doente.', 'posso', 'poder-conseguir', 'B1', '["posso", "sei", "consigo"]', 'Usamos ''poder'' para expressar impossibilidade por circunstâncias externas ou permissão.', 'We use ''poder'' to express impossibility due to external circumstances or permission.', 'Ми використовуємо ''poder'', щоб висловити неможливість через зовнішні обставини або дозвіл.', 'poder / conseguir', '0.5', '57', '2025-07-26 09:07:02.971896+01', '2025-08-11 17:01:43.837463+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('8254dd59-1e3b-4942-a1e8-1a2258a08165'::uuid, 'Embora ___ muito trabalho, vou ajudar-te.', 'tenha', 'presente-conjuntivo-irregulares', 'B2', '["tenha", "tinha", "tem", "tiver"]', 'Após ''embora'', usamos o presente do conjuntivo para expressar uma concessão no presente.', 'After ''embora'' (although), we use the present subjunctive to express a concession in the present.', 'Після ''embora'' (хоча) ми використовуємо теперішній час суб''єктивного способу для вираження поступки.', 'ter (eu)', '0.5', '57', '2025-07-26 09:05:31.141344+01', '2025-08-11 17:01:46.261659+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c29faa7d-1143-4fed-8394-b1536d7836e3'::uuid, 'Nós ___ uma casa grande.', 'temos', 'presente-indicativo-regulares', 'A1', '["temos", "falo", "falas", "fala"]', 'Usamos "temos" (present indicative).', 'We use "temos" (present indicative).', 'Ми використовуємо "temos" (present indicative).', 'ter', '0.5', '0', '2025-07-19 14:59:16.05993+01', '2025-08-10 21:24:24.118264+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('41ac3ee9-6282-415a-8b68-6e140bca1ae1'::uuid, 'Eu ___ muito feliz hoje.', 'estou', 'verbo-estar', 'A1', '["és", "sou", "está", "estou"]', 'Usamos ''estou'' para expressar estados temporários na primeira pessoa do singular.', 'We use ''estou'' (estar) for temporary states in first person singular.', 'Ми використовуємо ''estou'' для вираження тимчасового стану в першій особі однини.', 'estar', '0.5', '0', '2025-07-25 13:22:48.079979+01', '2025-08-10 21:24:24.122993+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('f26d65c0-c18d-496d-828f-a61a8f22f3e5'::uuid, 'Se ele ___ a verdade, tudo seria diferente.', 'soubesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["soube", "saberá", "soubesse", "sabe"]', 'Irrealidade presente: ''se'' + imperfeito do conjuntivo (''soubesse'') com condicional na principal.', 'Unreal present: ''se'' + imperfect subjunctive (''soubesse'') with conditional in the main clause.', 'Нереальність теперішнього: ''se'' + імперфект кон''юнктива (''soubesse'') та умовний у головній.', 'saber', '0.6', '57', '2025-08-10 20:28:53.061343+01', '2025-08-11 16:59:42.819791+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e0efee74-a16a-4e49-9dc8-10f90c12c560'::uuid, 'Ontem ___ muito no parque.', 'brinquei', 'preterito-perfeito-simples', 'A1', '["brincava", "brincando", "brinquei", "brinco"]', 'Usamos o pretérito perfeito para ações concluídas no passado.', 'We use the simple past for completed actions in the past.', 'Ми використовуємо простий минулий час для завершених дій у минулому.', 'brincar', '0.5', '0', '2025-07-25 15:50:29.288432+01', '2025-08-10 14:54:51.913433+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9a683cc0-8770-4558-830b-dad89bb7b10b'::uuid, 'Ele ___ a trabalhar agora.', 'está', 'presente-continuo', 'A1', '["estou", "estás", "está"]', 'No presente contínuo, usamos ''está'' com a terceira pessoa do singular.', 'In the present continuous, we use ''está'' with third person singular.', 'У теперішньому тривалому часі використовуємо ''está'' з третьою особою однини.', 'estar', '0.5', '0', '2025-07-25 15:50:29.241309+01', '2025-08-10 12:45:45.201317+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('096a4ecb-d298-46f6-bbf4-fea143783234'::uuid, 'É certo que ela ___ aqui.', 'está', 'indicativo-conjuntivo-infinitivo', 'B1', '["esteja", "está", "estar", "esteve"]', 'A resposta correta é "está". Usa-se indicativo para factos; conjuntivo para desejo/dúvida/ordem; infinitivo (pessoal) quando o sujeito é diferente em estruturas com ''para'', ''antes de'', etc. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "está". Use indicative for facts; subjunctive for wish/doubt/commands; (personal) infinitive when a different subject is marked with ''para'', ''antes de'', etc. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "está". Індикатив — для фактів; кон’юнктив — для бажання/сумніву/наказу; особовий інфінітив — коли підмет інший у структурах з ''para'', ''antes de'' тощо. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'indicativo de certeza', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0f03d1a4-ba73-43b1-89dc-9243da7dc641'::uuid, 'Para eles ___ melhor, precisamos explicar outra vez.', 'entenderem', 'indicativo-conjuntivo-infinitivo', 'B1', '["entendermos", "entenderem", "entender", "entende"]', 'A resposta correta é "entenderem". Usa-se indicativo para factos; conjuntivo para desejo/dúvida/ordem; infinitivo (pessoal) quando o sujeito é diferente em estruturas com ''para'', ''antes de'', etc. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "entenderem". Use indicative for facts; subjunctive for wish/doubt/commands; (personal) infinitive when a different subject is marked with ''para'', ''antes de'', etc. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "entenderem". Індикатив — для фактів; кон’юнктив — для бажання/сумніву/наказу; особовий інфінітив — коли підмет інший у структурах з ''para'', ''antes de'' тощо. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'infinitivo pessoal', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('87003fdf-0adf-4915-aeb0-19a738cb269e'::uuid, 'Embora ___ difícil, conseguiu acabar o trabalho.', 'fosse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["fosse", "com", "de", "o"]', 'Usamos "fosse" (imperfect subjunctive).', 'We use "fosse" (imperfect subjunctive).', 'Ми використовуємо "fosse" (imperfect subjunctive).', 'ser', '0.5', '57', '2025-07-19 14:59:16.066448+01', '2025-08-11 17:00:02.439245+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4e5daae2-f8b7-4a5b-91fa-9c73d11e4835'::uuid, 'Grande foi o espanto que ___ causou.', 'nos', 'hiperbatos-inversoes', 'C2', '["me", "o", "lhe", "nos"]', 'Hipérbatos e inversões alteram a ordem canónica por ênfase. Cuidado com a colocação pronominal em europeu: próclise, ênclise e mesóclise raríssima.', 'Hyperbatons invert canonical order for emphasis. Mind clitic placement in EP: proclisis/enclisis; mesoclisis is rare.', 'Гіпербатон змінює звичний порядок слів. Звертайте увагу на позицію клитиків у EP.', 'próclise obrigatória', '0.5', '0', '2025-08-10 22:10:07.15667+01', '2025-08-10 22:10:07.15667+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9ae9a2d0-319d-4cfb-9e27-95cdab89a0eb'::uuid, 'Os dados ___ por especialistas.', 'são analisados', 'voz-passiva', 'B1', '["são analisado", "é analisado", "foram analisados", "são analisados"]', 'A resposta correta é "são analisados". A voz passiva analítica forma-se com "ser" + particípio passado, concordando em género e número com o sujeito paciente. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "são analisados". Analytical passive voice is formed with "ser" + past participle, which agrees in gender and number with the patient subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "são analisados". Аналітичний пасив утворюється за допомогою "ser" + дієприкметник минулого, який узгоджується за родом і числом із підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'analisar', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:30.194403+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('f3b68d0e-b1d6-43a1-9084-3b8d5b7625c6'::uuid, 'Não ___ abrir a porta porque estava trancada.', 'consegui', 'poder-conseguir', 'B1', '["consigo", "consegui", "pude", "podia"]', '''Conseguir'' indica uma tentativa real que falhou, enquanto ''poder'' indicaria apenas possibilidade teórica.', '''Conseguir'' is used here because it indicates an actual attempt that failed, rather than theoretical possibility.', '''Conseguir'' вказує на реальну спробу, яка не вдалася, а не на теоретичну можливість.', 'poder / conseguir', '0.5', '57', '2025-07-26 09:05:31.228275+01', '2025-08-11 17:01:46.259699+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c911ba1e-6286-4c8b-b5e3-17b8430ffd27'::uuid, 'Acho que ele ___ sincero.', 'é', 'verbos-opiniao-sentimento', 'B1', '["será", "foi", "seja", "é"]', 'A resposta correta é "é". Verbos de opinião/sentimento como ''achar'', ''gostar'', ''temer'', ''duvidar'' influenciam o modo seguinte: com certeza → indicativo; com dúvida/emoção → conjuntivo. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "é". Opinion/feeling verbs like ''achar'', ''gostar'', ''temer'', ''duvidar'' affect mood: certainty → indicative; doubt/emotion → subjunctive. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "é". Дієслова думки/почуттів, як-от ''achar'', ''gostar'', ''temer'', ''duvidar'', впливають на спосіб: упевненість → індикатив; сумнів/емоція → кон’юнктив. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'opinião com certeza', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('5f4b06f7-8037-4ca6-991a-3be10207109f'::uuid, 'Apenas mais tarde se me ___ o sentido oculto.', 'revelou', 'hiperbatos-inversoes', 'C2', '["revela", "revelara", "revelaria", "revelou"]', 'Hipérbatos e inversões alteram a ordem canónica por ênfase. Cuidado com a colocação pronominal em europeu: próclise, ênclise e mesóclise raríssima.', 'Hyperbatons invert canonical order for emphasis. Mind clitic placement in EP: proclisis/enclisis; mesoclisis is rare.', 'Гіпербатон змінює звичний порядок слів. Звертайте увагу на позицію клитиків у EP.', 'inversão verbo-sujeito', '0.5', '0', '2025-08-10 22:10:07.157017+01', '2025-08-10 22:10:07.157017+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6e5d5247-6065-4f64-958f-9750a56f6310'::uuid, 'É possível que ela já ___ chegado a casa.', 'tenha', 'preterito-perfeito-composto-conjuntivo', 'B2', '["tenha", "tem", "tinha", "tiver"]', 'Após expressões de possibilidade, usamos o pretérito perfeito composto do conjuntivo.', 'After expressions of possibility, we use the present perfect subjunctive.', 'Після виразів можливості ми використовуємо складений перфект кон''юнктива.', 'ter', '0.5', '0', '2025-07-26 09:07:02.976872+01', '2025-08-10 12:19:39.244551+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('2845c96d-c275-4b86-91d6-8ac574f2c517'::uuid, 'Ela perguntou: "Onde moras?" → Ela perguntou onde eu ___ .', 'morava', 'discurso-direto-indireto', 'B1', '["morei", "morava", "morarei", "moro"]', 'A resposta correta é "morava". No discurso indireto, os tempos e pronomes podem mudar: presente → pretérito imperfeito, futuro → condicional, pronomes e advérbios ajustam-se ao ponto de referência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "morava". In reported speech, tenses and pronouns may shift: present → imperfect past, future → conditional; pronouns/adverbs adjust to the new reference point. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "morava". У непрямій мові часи та займенники можуть змінюватися: теперішній → недоконаний минулий, майбутній → умовний; займенники/прислівники узгоджуються з новою точкою відліку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'morar', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c7619c07-e077-4ac7-84b3-2c49a8e7c6d4'::uuid, 'Embora ele ___ muito dinheiro, vive de forma simples.', 'tenha', 'presente-conjuntivo-irregulares', 'B2', '["tenha", "tem", "tiver", "tivesse"]', 'Após ''embora'', usamos o presente do conjuntivo para expressar contraste.', 'After ''embora'' (although), we use the present subjunctive to express contrast.', 'Після ''embora'' (хоча) використовуємо теперішній час кон''юнктива для вираження контрасту.', 'ter', '0.5', '56', '2025-07-25 13:25:43.080198+01', '2025-08-11 17:01:27.826144+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6eaf59af-276d-4cfc-97f7-e4f376fe8e64'::uuid, 'Tais palavras, aos presentes, ___ ficaram na memória.', 'lhes', 'hiperbatos-inversoes', 'C2', '["os", "nos", "lhes", "se"]', 'Hipérbatos e inversões alteram a ordem canónica por ênfase. Cuidado com a colocação pronominal em europeu: próclise, ênclise e mesóclise raríssima.', 'Hyperbatons invert canonical order for emphasis. Mind clitic placement in EP: proclisis/enclisis; mesoclisis is rare.', 'Гіпербатон змінює звичний порядок слів. Звертайте увагу на позицію клитиків у EP.', 'dat. ético clítico', '0.5', '0', '2025-08-10 22:10:07.157221+01', '2025-08-10 22:10:07.157221+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('5e8ef280-a074-4e3d-836e-792d0e1f4ee9'::uuid, 'Ele prometeu: "Estarei lá" → Ele prometeu que ___ lá.', 'estaria', 'discurso-direto-indireto', 'B1', '["estará", "esteve", "estaria", "estava"]', 'A resposta correta é "estaria". No discurso indireto, os tempos e pronomes podem mudar: presente → pretérito imperfeito, futuro → condicional, pronomes e advérbios ajustam-se ao ponto de referência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "estaria". In reported speech, tenses and pronouns may shift: present → imperfect past, future → conditional; pronouns/adverbs adjust to the new reference point. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "estaria". У непрямій мові часи та займенники можуть змінюватися: теперішній → недоконаний минулий, майбутній → умовний; займенники/прислівники узгоджуються з новою точкою відліку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'estar', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('aacaedbd-aa18-42ba-acac-68b7a76f562a'::uuid, 'Ela comentou: "Trabalho aqui" → Ela comentou que ___ ali.', 'trabalhava', 'discurso-direto-indireto', 'B1', '["tem trabalhado", "trabalhava", "trabalhou", "trabalhará"]', 'A resposta correta é "trabalhava". No discurso indireto, os tempos e pronomes podem mudar: presente → pretérito imperfeito, futuro → condicional, pronomes e advérbios ajustam-se ao ponto de referência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "trabalhava". In reported speech, tenses and pronouns may shift: present → imperfect past, future → conditional; pronouns/adverbs adjust to the new reference point. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "trabalhava". У непрямій мові часи та займенники можуть змінюватися: теперішній → недоконаний минулий, майбутній → умовний; займенники/прислівники узгоджуються з новою точкою відліку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'trabalhar', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('34c5ed87-2460-416a-8bb7-1ebf2139bd3f'::uuid, 'Ela ___ levantou muito cedo hoje.', 'se', 'colocacao-pronominal', 'B1', '["o", "se", "me", "te"]', 'A resposta correta é "se". Em Portugal, a ênclise é frequente com verbos no infinitivo/imperativo afirmativo; a próclise ocorre com palavras atrativas (não, que, se, advérbios, pronomes relativos). O pronome deve ocupar a posição correta segundo a regra. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "se". In European Portuguese, enclisis is common with infinitives/affirmative imperatives; proclisis occurs with attractors (não, que, se, adverbs, relative pronouns). Place the clitic pronoun according to the rule. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "se". В європейській португальській енкліза вживається з інфінітивом/стверджувальним наказовим, прокліза — з атракторами (não, que, se, прислівниками, відносними займенниками). Займенник має стояти у правильній позиції. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'próclise com advérbio implícito', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('14642674-e1aa-4ce9-8e2a-20def87c4f4b'::uuid, 'Podem ___ ajudar com estas caixas?', 'me', 'colocacao-pronominal', 'B1', '["me", "te", "lhe", "o"]', 'A resposta correta é "me". Em Portugal, a ênclise é frequente com verbos no infinitivo/imperativo afirmativo; a próclise ocorre com palavras atrativas (não, que, se, advérbios, pronomes relativos). O pronome deve ocupar a posição correta segundo a regra. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "me". In European Portuguese, enclisis is common with infinitives/affirmative imperatives; proclisis occurs with attractors (não, que, se, adverbs, relative pronouns). Place the clitic pronoun according to the rule. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "me". В європейській португальській енкліза вживається з інфінітивом/стверджувальним наказовим, прокліза — з атракторами (não, que, se, прислівниками, відносними займенниками). Займенник має стояти у правильній позиції. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'ênclise/proclise aceitável; aqui próclise', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('bb6d34f1-167d-475e-838f-0c8c68f706cd'::uuid, 'Tu ___ muito bem português.', 'falas', 'presente-indicativo-regulares', 'A1', '["falo", "fala", "falas"]', 'Com ''tu'', usamos a terminação ''-as'' nos verbos regulares da primeira conjugação.', 'With ''tu'', we use the ending ''-as'' for regular first conjugation verbs.', 'З ''tu'' ми використовуємо закінчення ''-as'' для правильних дієслів першої дієвідміни.', 'falar', '0.5', '0', '2025-07-25 15:50:30.593963+01', '2025-08-10 21:24:24.114943+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('5f12a574-8557-4ed4-abe6-1457ca31cf32'::uuid, 'Do salão saíram, em fila, ___ convidados ilustres.', 'os', 'hiperbatos-inversoes', 'C2', '["os", "estes", "uns", "aqueles"]', 'Hipérbatos e inversões alteram a ordem canónica por ênfase. Cuidado com a colocação pronominal em europeu: próclise, ênclise e mesóclise raríssima.', 'Hyperbatons invert canonical order for emphasis. Mind clitic placement in EP: proclisis/enclisis; mesoclisis is rare.', 'Гіпербатон змінює звичний порядок слів. Звертайте увагу на позицію клитиків у EP.', 'artigo retomado na inversão', '0.5', '0', '2025-08-10 22:10:07.157569+01', '2025-08-10 22:10:07.157569+01');
