INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('539dd535-8697-4af8-9650-a2f711fb56f7'::uuid, 'O relatório ___ te referiste está na pasta.', 'a que', 'pronomes-relativos', 'B1', '["de que", "a que", "em que", "que"]', 'A resposta correta é "a que". Pronomes relativos como ''que'', ''quem'', ''o qual'', ''cujo'' ligam orações e retoman um antecedente, respeitando concordância e regência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "a que". Relative pronouns like ''que'', ''quem'', ''o qual'', ''cujo'' link clauses and refer back to an antecedent, observing agreement and government. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "a que". Відносні займенники ''que'', ''quem'', ''o qual'', ''cujo'' поєднують частини речення і відсилають до антецедента з дотриманням керування і узгодження. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'regência específica', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('338a263c-6241-44be-a9be-c63cf4f1fcfa'::uuid, 'Não acredito que isso ___ verdade.', 'seja', 'verbos-opiniao-sentimento', 'B1', '["seja", "é", "foi", "será"]', 'A resposta correta é "seja". Verbos de opinião/sentimento como ''achar'', ''gostar'', ''temer'', ''duvidar'' influenciam o modo seguinte: com certeza → indicativo; com dúvida/emoção → conjuntivo. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "seja". Opinion/feeling verbs like ''achar'', ''gostar'', ''temer'', ''duvidar'' affect mood: certainty → indicative; doubt/emotion → subjunctive. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "seja". Дієслова думки/почуттів, як-от ''achar'', ''gostar'', ''temer'', ''duvidar'', впливають на спосіб: упевненість → індикатив; сумнів/емоція → кон’юнктив. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'negação de crença → conjuntivo', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
