INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('2d345c67-ae98-4eb9-bb61-363835778abc'::uuid, 'Ele ___ levantar esse peso sozinho.', 'consegue', 'poder-conseguir', 'B1', '["conseguia", "conseguir", "consegue", "pode"]', 'A resposta correta é "consegue". Usa-se "poder" para permissão/possibilidade e "conseguir" para capacidade/resultado alcançado. No presente, a forma deve concordar com o sujeito. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "consegue". Use "poder" for permission/possibility and "conseguir" for ability/result achieved. In the present tense, the form must agree with the subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "consegue". "poder" вживається для дозволу/можливості, а "conseguir" — для спроможності/результату. У теперішньому часі форма має узгоджуватися з підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'poder / conseguir', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:30.3672+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c725af9a-63f2-4ec9-b177-871d3a44ec30'::uuid, 'Se eu te ___ antes, explicava tudo com calma.', 'visse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["veria", "vi", "vejo", "visse"]', 'O imperfeito do conjuntivo de ''ver'' é ''visse'' para condições irreais.', 'The imperfect subjunctive of ''ver'' is ''visse'' for unreal conditions.', 'Імперфект кон''юнктива ''ver'' — ''visse'' у нереальних умовах.', NULL, '0.6', '57', '2025-08-10 20:28:53.061343+01', '2025-08-11 17:01:38.851555+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b553d754-129c-4bf9-8cac-646138d468b6'::uuid, 'Você não ___ estacionar aqui, é proibido.', 'pode', 'poder-conseguir', 'B1', '["podia", "conseguia", "pode", "consegue"]', '''Poder'' é usado para expressar permissão. Como é proibido, usamos ''não pode''.', '''Poder'' is used to express permission. Since it is forbidden, we use ''não pode''.', '''Poder'' використовується для вираження дозволу. Оскільки це заборонено, ми кажемо ''não pode''.', 'poder / conseguir', '0.6', '57', '2025-08-10 16:07:41.89204+01', '2025-08-11 17:01:41.695079+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('78947264-d126-4895-bb05-506cf5ad5bd3'::uuid, 'Logo que ___ o pagamento, enviamos a fatura final.', 'recebermos', 'futuro-conjuntivo-conjuncoes', 'B2', '["receberemos", "recebemos", "recebamos", "recebermos"]', 'Com ''logo que'' projetado no futuro, usa-se futuro do conjuntivo: ''recebermos''.', 'With ''logo que'' projected to the future, use the future subjunctive: ''recebermos''.', 'Із ''logo que'' щодо майбутнього вживається майбутній кон''юнктив: ''recebermos''.', NULL, '0.6', '57', '2025-08-10 20:28:53.067197+01', '2025-08-11 16:59:42.827149+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4b5b4572-1b7e-4fb7-ae08-6b75838476e0'::uuid, 'Se eu ___ mais tempo, teria ido à festa.', 'tivesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["tenha", "tiver", "tinha", "tivesse"]', 'Em condicionais irreais no passado, usamos o imperfeito do conjuntivo após ''se''.', 'In unreal conditional sentences about the past, we use the imperfect subjunctive after ''se''.', 'У нереальних умовних реченнях про минуле після ''se'' вживається імперфект суб''юнктива.', NULL, '0.5', '57', '2025-07-26 09:07:03.28985+01', '2025-08-11 17:01:42.901879+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('63d45d02-d86a-4117-8024-385266bc1b12'::uuid, 'A decisão ___ tomada pelo conselho administrativo.', 'foi', 'voz-passiva', 'B2', '["foi", "era", "seria", "tinha sido"]', 'Na voz passiva, usamos ''ser'' no tempo adequado + particípio passado do verbo principal.', 'In passive voice, we use ''ser'' in the appropriate tense + past participle of the main verb.', 'У пасивному стані ми використовуємо ''ser'' у відповідному часі + дієприкметник минулого часу основного дієслова.', 'tomar', '0.5', '56', '2025-07-25 08:56:04.129618+01', '2025-08-11 17:01:27.823629+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0d506fde-e702-4840-97e3-3cd272446118'::uuid, 'Espero que ele ___ cedo.', 'chege', 'presente-conjuntivo-regulares', 'B1', '["chege", "chega", "chegou", "chegaria"]', 'A resposta correta é "chege". O presente do conjuntivo usa-se após expressões de vontade, dúvida, necessidade, entre outras. Regularmente, -ar termina em -e/-es/-e/-emos/-em; -er/-ir em -a/-as/-a/-amos/-am. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "chege". Present subjunctive is used after expressions of wish, doubt, necessity, etc. Regular verbs: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "chege". Теперішній кон’юнктив уживається після виразів бажання, сумніву, необхідності тощо. Регулярні: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'chegar', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:27.82554+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('d96b64f0-2ca4-4806-9380-098ce8bb348e'::uuid, 'Prefiro café ___ chá ao pequeno-almoço.', 'a', 'regencia-casos-complexos', 'C2', '["de", "do", "para", "a"]', 'Regência: verbos e nomes exigem preposições específicas em PT europeu: «assistir a», «obedecer a», «preferir X a Y», «simpatizar com».', 'Government: verbs/nouns require specific prepositions in EP: «assistir a», «obedecer a», «preferir X a Y», «simpatizar com».', 'Керування: дієслова/іменники вимагають певних прийменників у EP.', 'preferir X a Y', '0.5', '0', '2025-08-10 22:10:07.163255+01', '2025-08-10 22:10:07.163255+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4acb1452-787a-4ccc-bb0a-a87068b92f43'::uuid, 'Obedeceu ___ ordens sem hesitar.', 'às', 'regencia-casos-complexos', 'C2', '["as", "às", "aos", "com as"]', 'Regência: verbos e nomes exigem preposições específicas em PT europeu: «assistir a», «obedecer a», «preferir X a Y», «simpatizar com».', 'Government: verbs/nouns require specific prepositions in EP: «assistir a», «obedecer a», «preferir X a Y», «simpatizar com».', 'Керування: дієслова/іменники вимагають певних прийменників у EP.', 'obedecer a', '0.5', '0', '2025-08-10 22:10:07.163576+01', '2025-08-10 22:10:07.163576+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('72b0f147-d721-415e-a90a-d5080fb229f0'::uuid, 'O relatório ___ pelo diretor ontem.', 'foi aprovado', 'voz-passiva', 'B1', '["era aprovado", "foi aprovado", "foi aprovada", "foi aprovar"]', 'A resposta correta é "foi aprovado". A voz passiva analítica forma-se com "ser" + particípio passado, concordando em género e número com o sujeito paciente. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "foi aprovado". Analytical passive voice is formed with "ser" + past participle, which agrees in gender and number with the patient subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "foi aprovado". Аналітичний пасив утворюється за допомогою "ser" + дієприкметник минулого, який узгоджується за родом і числом із підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'aprovar', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:30.193651+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('40af3567-0d15-488d-8e15-19f5c46e8c0b'::uuid, 'Ela ___ em casa agora.', 'está', 'verbo-estar', 'A1', '["está", "é", "estás", "estão"]', 'Usamos ''está'' (3ª pessoa singular do verbo estar) para indicar localização temporária.', 'We use ''está'' (3rd person singular of estar) to indicate temporary location.', 'Ми використовуємо ''está'' (3-тя особа однини дієслова estar) для позначення тимчасового місцезнаходження.', 'estar', '0.5', '0', '2025-07-25 15:50:30.592037+01', '2025-08-10 21:24:24.115308+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('045dc384-d4f2-461e-a428-ed27ad9571cc'::uuid, 'Quando ___ a Lisboa, vou visitar o Mosteiro dos Jerónimos.', 'for', 'futuro-conjuntivo-conjuncoes', 'B2', '["vá", "irei", "vou", "for"]', 'Com ''quando'' referindo-se ao futuro, usamos o futuro do conjuntivo.', 'With ''quando'' (when) referring to the future, we use the future subjunctive.', 'З ''quando'' (коли), що відноситься до майбутнього, ми використовуємо майбутній час суб''юнктива.', 'ir', '0.5', '57', '2025-07-26 16:16:11.024417+01', '2025-08-11 17:01:42.899422+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6161e80d-7616-46be-ae60-01f4c9fde0bb'::uuid, 'Quero que tu ___ cá amanhã cedo.', 'estejas', 'presente-conjuntivo-irregulares', 'B2', '["estejas", "estás", "estarás", "estivesses"]', 'Após ''querer que'', usamos o presente do conjuntivo para expressar desejo.', 'After ''querer que'', we use present subjunctive to express desire.', 'Після ''querer que'' вживається теперішній час суб''юнктива для вираження бажання.', 'estar', '0.5', '57', '2025-07-26 09:07:03.292951+01', '2025-08-11 17:01:42.900223+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ce158a34-ca32-449e-8648-ca9d1ed2faed'::uuid, 'Tomara que ___ tudo bem no exame!', 'corra', 'presente-conjuntivo-irregulares', 'B2', '["corria", "corra", "corre", "correr"]', 'Após ''tomara que'', expressando desejo, usa-se o presente do conjuntivo.', 'After ''tomara que'', expressing wish, we use the present subjunctive.', 'Після ''tomara que'', що виражає бажання, ми використовуємо теперішній час умовного способу.', 'correr', '0.5', '57', '2025-07-26 09:05:35.42742+01', '2025-08-11 17:01:43.839748+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0092846f-f2d7-400e-a980-550d9fc49cf1'::uuid, 'Se eu ___ mais tempo, teria acabado o trabalho.', 'tivesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["tenha", "tivesse", "tinha", "tiver"]', 'Em condições irreais no passado, usa-se o imperfeito do conjuntivo após ''se''.', 'In unreal past conditions, we use the imperfect subjunctive after ''se''.', 'У нереальних умовах минулого після ''se'' використовуємо імперфект суб''юнктива.', NULL, '0.5', '57', '2025-07-26 09:05:31.230648+01', '2025-08-11 17:01:44.370911+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('23afac66-1f1b-4fe3-9480-55ba5d52daad'::uuid, 'Assim que ___ os documentos, enviarei por email.', 'receber', 'futuro-conjuntivo-conjuncoes', 'B2', '["receberia", "recebi", "receba", "receber"]', 'Com ''assim que'' referindo-se ao futuro, usamos o futuro do conjuntivo.', 'With ''assim que'' (as soon as) referring to the future, we use the future subjunctive.', 'З ''assim que'' (як тільки) щодо майбутнього використовуємо майбутній час кон''юнктива.', 'receber (eu)', '0.5', '56', '2025-07-25 13:51:24.434681+01', '2025-08-11 17:01:26.9408+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('daff0680-2188-4fdb-8cea-676231304c82'::uuid, 'Embora ele ___ que está errado, não quer admitir.', 'saiba', 'presente-conjuntivo-irregulares', 'B2', '["soubesse", "sabe", "saber", "saiba"]', 'Após ''embora'', usamos o presente do conjuntivo do verbo ''saber''.', 'After ''embora'' (although), we use the present subjunctive of the verb ''saber''.', 'Після ''embora'' (хоча) ми використовуємо теперішній час суб''юнктива дієслова ''saber''.', 'saber (ele/ela)', '0.5', '56', '2025-07-25 13:25:45.48339+01', '2025-08-11 17:01:27.201208+01');
