INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('56c82663-c477-4656-9188-c16561afa17f'::uuid, 'Ele carece ___ recursos para levar o plano avante.', 'de', 'regencia-casos-complexos', 'C2', '["para", "de", "dos", "a"]', 'Regência: verbos e nomes exigem preposições específicas em PT europeu: «assistir a», «obedecer a», «preferir X a Y», «simpatizar com».', 'Government: verbs/nouns require specific prepositions in EP: «assistir a», «obedecer a», «preferir X a Y», «simpatizar com».', 'Керування: дієслова/іменники вимагають певних прийменників у EP.', 'carecer de', '0.5', '0', '2025-08-10 22:10:07.164023+01', '2025-08-10 22:10:07.164023+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('2d8667ce-5f47-4989-8cdf-c50c1ab4642b'::uuid, 'Ele ___ o caminho até ao museu.', 'sabe', 'saber-conhecer', 'B1', '["conhece", "saber", "sabia", "sabe"]', 'A resposta correta é "sabe". "Saber" indica conhecimento de factos ou habilidade; "conhecer" indica familiaridade com pessoas, lugares ou coisas. O verbo deve concordar com o sujeito no presente. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "sabe". "Saber" is used for knowledge of facts or skills; "conhecer" is for familiarity with people, places, or things. Use the present form that agrees with the subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "sabe". "saber" вживається для фактів або умінь; "conhecer" — для знайомства з людьми, місцями чи речами. Потрібна форма теперішнього часу узгоджується з підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'saber / conhecer', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:30.195255+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c47a6dec-0840-492c-a354-d11b114354c5'::uuid, 'É possível que ele ___ ajudar.', 'possa', 'presente-conjuntivo-irregulares', 'B1', '["possa", "poderia", "pode", "pôde"]', 'A resposta correta é "possa". Verbos irregulares no presente do conjuntivo: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "possa". Irregular verbs in the present subjunctive: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "possa". Неправильні дієслова в теперішньому кон’юнктиві: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'poder', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:30.195587+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('32eda04a-996d-42c2-ba8e-9bd2d561df29'::uuid, 'Rezo para que ele ___ forças.', 'tenha', 'presente-conjuntivo-irregulares', 'B1', '["tenha", "teve", "tem", "teria"]', 'A resposta correta é "tenha". Verbos irregulares no presente do conjuntivo: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "tenha". Irregular verbs in the present subjunctive: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "tenha". Неправильні дієслова в теперішньому кон’юнктиві: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'ter', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:30.198239+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3ff1c6fe-f1fc-4c82-be26-7c2c353373cf'::uuid, 'Se vocês ___ o aviso, não teria havido confusão.', 'tivessem lido', 'se-preterito-imperfeito-conjuntivo', 'B2', '["tivessem lido", "tinham lido", "leriam", "leram"]', 'Para condição contrafactual no passado: ''se'' + mais-que-perfeito composto do conjuntivo (''tivessem lido'').', 'For counterfactual past condition: ''se'' + pluperfect subjunctive compound (''tivessem lido'').', 'Для контрфактичного минулого: ''se'' + складений плюсквамперфект кон''юнктива (''tivessem lido'').', NULL, '0.6', '57', '2025-08-10 20:28:53.061343+01', '2025-08-11 17:01:38.855287+01');
