INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('194fa4b6-bc53-4458-961d-9f07e7dbb030'::uuid, 'Os bilhetes ___ online rapidamente.', 'são comprados', 'voz-passiva', 'B1', '["são comprado", "é comprado", "são comprados", "foram comprados"]', 'A resposta correta é "são comprados". A voz passiva analítica forma-se com "ser" + particípio passado, concordando em género e número com o sujeito paciente. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "são comprados". Analytical passive voice is formed with "ser" + past participle, which agrees in gender and number with the patient subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "são comprados". Аналітичний пасив утворюється за допомогою "ser" + дієприкметник минулого, який узгоджується за родом і числом із підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'comprar', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:30.373248+01');
