INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('1efbf780-bda0-4ceb-988e-08ad32b7c909'::uuid, 'Oxalá tu ___ um ótimo dia hoje!', 'tenhas', 'frases-exclamativas-desejo', 'B2', '["terás", "tenhas", "tivesses", "tens"]', 'Desejo positivo atual: presente do conjuntivo ''tenhas''.', 'Positive present wish: present subjunctive ''tenhas''.', 'Позитивне побажання теперішнього: презент кон''юнктива ''tenhas''.', NULL, '0.6', '0', '2025-08-10 20:28:53.078997+01', '2025-08-10 20:28:53.078997+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('fe878989-1657-4fd0-a6b3-a25e6c3971d5'::uuid, 'Tomara que as coisas ___ depressa!', 'andem', 'frases-exclamativas-desejo', 'B2', '["andassem", "andam", "andem", "andarão"]', 'Com ''tomara'', usa-se presente do conjuntivo: ''andem''.', 'With ''tomara'', use the present subjunctive: ''andem''.', 'З ''tomara'' вживається презент кон''юнктива: ''andem''.', NULL, '0.6', '0', '2025-08-10 20:28:53.078997+01', '2025-08-10 20:28:53.078997+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('03900fb6-5918-46cc-a796-965a2cabe088'::uuid, 'Tu ___ o livro na mochila.', 'tens', 'verbo-ter', 'A1', '["temos", "tem", "tens"]', 'Com a segunda pessoa singular (tu), usamos ''tens'' para expressar posse.', 'With second person singular (you), we use ''tens'' to express possession.', 'З другою особою однини (ти) ми використовуємо ''tens'' для вираження володіння.', 'ter', '0.5', '0', '2025-07-25 15:50:31.615622+01', '2025-08-10 21:24:24.11377+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6085180f-99a1-43a8-bd16-ef194a77673b'::uuid, 'Convém que nós ___ os detalhes.', 'resolvamos', 'presente-conjuntivo-regulares', 'B1', '["resolvemos", "resolver", "resolvêramos", "resolvamos"]', 'A resposta correta é "resolvamos". O presente do conjuntivo usa-se após expressões de vontade, dúvida, necessidade, entre outras. Regularmente, -ar termina em -e/-es/-e/-emos/-em; -er/-ir em -a/-as/-a/-amos/-am. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "resolvamos". Present subjunctive is used after expressions of wish, doubt, necessity, etc. Regular verbs: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "resolvamos". Теперішній кон’юнктив уживається після виразів бажання, сумніву, необхідності тощо. Регулярні: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'resolver', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:32.163541+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('1d2017b1-c275-4496-9cdb-e8afe726ec9f'::uuid, 'Se eu ___ mais tempo, teria feito um trabalho melhor.', 'tivesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["tenha", "tivesse", "tiver", "tinha"]', 'Nas condicionais irreais do passado, usamos o imperfeito do conjuntivo após ''se''.', 'In unreal past conditionals, we use the imperfect subjunctive after ''se'' (if).', 'У нереальних умовних реченнях минулого часу після ''se'' (якщо) використовуємо імперфект кон''юнктива.', NULL, '0.5', '56', '2025-07-25 13:25:43.074022+01', '2025-08-11 17:01:27.822153+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4064e7b6-7a9d-42c1-b2ac-894ee1028100'::uuid, 'Se ele ___ estudado, teria passado no exame.', 'tivesse', 'mais-que-perfeito-composto-conjuntivo', 'B2', '["tivesse", "em", "para", "de"]', 'Usamos "tivesse" (pluperfect subjunctive).', 'We use "tivesse" (pluperfect subjunctive).', 'Ми використовуємо "tivesse" (pluperfect subjunctive).', NULL, '0.5', '0', '2025-07-19 14:59:16.066785+01', '2025-08-10 10:58:29.198965+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('bbeddf73-4afd-4656-9f8d-d27a2c296a39'::uuid, 'Para ___ cedo, precisamos de nos deitar antes.', 'chegarmos', 'indicativo-conjuntivo-infinitivo', 'B1', '["chegarmos", "chegar", "chegarem", "chegarmos-nos"]', 'A resposta correta é "chegarmos". Usa-se indicativo para factos; conjuntivo para desejo/dúvida/ordem; infinitivo (pessoal) quando o sujeito é diferente em estruturas com ''para'', ''antes de'', etc. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "chegarmos". Use indicative for facts; subjunctive for wish/doubt/commands; (personal) infinitive when a different subject is marked with ''para'', ''antes de'', etc. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "chegarmos". Індикатив — для фактів; кон’юнктив — для бажання/сумніву/наказу; особовий інфінітив — коли підмет інший у структурах з ''para'', ''antes de'' тощо. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'infinitivo pessoal', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('2d897e50-3583-4d9e-b6e2-bb1e126755fd'::uuid, 'Se eu ___ rico, compraria uma casa.', 'fosse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["fosse", "de", "um", "com"]', 'Usamos "fosse" (imperfect subjunctive).', 'We use "fosse" (imperfect subjunctive).', 'Ми використовуємо "fosse" (imperfect subjunctive).', 'ser', '0.5', '57', '2025-07-19 14:59:16.064814+01', '2025-08-11 17:00:02.44231+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('1dbcf519-e5ba-44d3-ab9d-49751ba22d60'::uuid, 'Se eu ___ mais tempo, teria estudado melhor para o exame.', 'tivesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["tivesse", "tinha", "tiver", "tenha"]', 'Em condições irreais no passado, usamos o imperfeito do conjuntivo após ''se''.', 'In unreal past conditions, we use the imperfect subjunctive after ''se'' (if).', 'У нереальних умовах минулого після ''se'' (якби) використовуємо імперфект кон''юнктива.', NULL, '0.5', '56', '2025-07-25 13:51:24.430867+01', '2025-08-11 17:01:26.944445+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4c1caff9-abe2-4b86-a10e-fb256cc00855'::uuid, 'Prefiro ___ montanha lá ao fundo.', 'aquela', 'pronomes-demonstrativos', 'B1', '["essa", "aquilo", "aquela", "esta"]', 'A resposta correta é "aquela". Este/esta/isto referem-se a algo perto do falante; esse/essa/isso a algo perto do ouvinte; aquele/aquela/aquilo a algo distante de ambos. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "aquela". Este/esta/isto refer to something near the speaker; esse/essa/isso to something near the listener; aquele/aquela/aquilo to something far from both. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "aquela". Este/esta/isto — близько до мовця; esse/essa/isso — близько до слухача; aquele/aquela/aquilo — далеко від обох. Ця форма відповідає контексту і зберігає граматичну правильність речення.', '', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('27dddfb2-c7ba-45eb-8fe1-9d7c26f22890'::uuid, 'Disseram: "Podemos ajudar" → Disseram que ___ ajudar.', 'podiam', 'discurso-direto-indireto', 'B1', '["puderam", "podemos", "poderiam", "podiam"]', 'A resposta correta é "podiam". No discurso indireto, os tempos e pronomes podem mudar: presente → pretérito imperfeito, futuro → condicional, pronomes e advérbios ajustam-se ao ponto de referência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "podiam". In reported speech, tenses and pronouns may shift: present → imperfect past, future → conditional; pronouns/adverbs adjust to the new reference point. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "podiam". У непрямій мові часи та займенники можуть змінюватися: теперішній → недоконаний минулий, майбутній → умовний; займенники/прислівники узгоджуються з новою точкою відліку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'poder', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('49febe5a-7858-458e-a6e9-3a6315e6d3f0'::uuid, 'Não ___ ajudá-lo por estar doente.', 'consegui', 'poder-conseguir', 'B1', '["consigo", "consegui", "pude", "podia"]', 'Usamos ''conseguir'' quando se trata de capacidade física ou habilidade específica para realizar algo.', 'We use ''conseguir'' when referring to physical ability or specific capability to accomplish something.', 'Ми використовуємо ''conseguir'', коли йдеться про фізичну здатність або конкретну можливість щось зробити.', 'poder / conseguir', '0.5', '57', '2025-07-25 08:56:04.122515+01', '2025-08-11 17:00:02.432117+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('1fb5a19b-f3a1-42d1-a1d4-e1ca1fa3abe5'::uuid, 'Não ___ dormir por causa do barulho.', 'consegui', 'poder-conseguir', 'B1', '["consegui", "pude", "podia", "consigo"]', 'Usamos ''conseguir'' para expressar uma capacidade específica que não foi alcançada devido a um impedimento concreto.', 'We use ''conseguir'' to express a specific ability that wasn''t achieved due to a concrete impediment.', 'Ми використовуємо ''conseguir'' для вираження конкретної здатності, яка не була досягнута через певну перешкоду.', 'poder / conseguir', '0.5', '57', '2025-07-26 09:05:34.542914+01', '2025-08-11 17:01:44.373618+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('13ecec82-46c3-4045-8832-3e8b2fdc8ad2'::uuid, 'É importante que todos ___ a ata antes da reunião.', 'leiam', 'conjuntivo-geral', 'B2', '["leem", "leiam", "leram", "ler"]', 'Usa-se o conjuntivo presente depois de expressões de necessidade/valoração como ''é importante que''. O verbo ''ler'' no conjuntivo presente, 3.ª pessoa plural, é ''leiam''.', 'Use the present subjunctive after expressions of necessity like ''é importante que''. The verb ''ler'' in present subjunctive, 3rd person plural, is ''leiam''.', 'Після виразів потреби на кшталт ''é importante que'' вживається презент конжунктива. Дієслово ''ler'' у цій формі множини — ''leiam''.', NULL, '0.6', '0', '2025-08-10 20:28:53.053116+01', '2025-08-10 20:28:53.053116+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('efb26096-44bd-4aa9-9d8a-484a5b8aea1e'::uuid, 'Embora ___ chover, vamos fazer o piquenique.', 'possa', 'presente-conjuntivo-irregulares', 'B2', '["pudesse", "poderá", "pode", "possa"]', '''Embora'' exige o presente do conjuntivo para expressar uma possibilidade no presente.', '''Embora'' requires present subjunctive to express a present possibility.', 'Після ''embora'' вживається теперішній час суб''юнктива для вираження теперішньої можливості.', 'poder', '0.5', '57', '2025-07-26 09:07:03.291878+01', '2025-08-11 17:01:42.904478+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('957b3f7c-020a-4c0f-a6d8-c0e24e6649de'::uuid, 'É fundamental que ___ as regras do jogo antes de começar.', 'saibamos', 'presente-conjuntivo-irregulares', 'B2', '["soubemos", "sabermos", "sabemos", "saibamos"]', 'Após ''é fundamental que'', usamos o presente do conjuntivo para expressar necessidade.', 'After ''é fundamental que'' (it is fundamental that), we use the present subjunctive to express necessity.', 'Після ''é fundamental que'' (важливо, щоб) ми використовуємо теперішній час суб''юнктива для вираження необхідності.', 'saber', '0.5', '57', '2025-07-26 16:16:11.023705+01', '2025-08-11 17:01:42.900655+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('68d1f846-39ab-4bf1-b1f3-66ed7caa1b8d'::uuid, 'Quando ___ a Lisboa, visitarei o Mosteiro dos Jerónimos.', 'vier', 'futuro-conjuntivo-conjuncoes', 'B2', '["vem", "venho", "viria", "vier"]', 'Com ''quando'' referindo-se ao futuro, usamos o futuro do conjuntivo.', 'With ''quando'' referring to the future, we use the future subjunctive.', 'З ''quando'', що відноситься до майбутнього, ми використовуємо майбутній час кон''юнктива.', 'vir', '0.5', '57', '2025-07-26 09:07:02.976396+01', '2025-08-11 17:01:43.837094+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c2ae8aff-2dab-465f-86f5-09cdf1a6f5bc'::uuid, 'Não ___ ir à reunião hoje porque estou doente.', 'posso', 'poder-conseguir', 'B1', '["consigo", "posso", "devo", "sei"]', 'Usamos ''poder'' para expressar impossibilidade devido a circunstâncias externas ou permissão.', 'We use ''poder'' to express impossibility due to external circumstances or permission, not physical ability.', 'Ми використовуємо ''poder'' для вираження неможливості через зовнішні обставини або дозвіл.', 'poder / conseguir', '0.5', '57', '2025-07-26 09:05:31.137232+01', '2025-08-11 17:01:46.259072+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('59ff3c88-0157-4bb3-a8d3-61d50c0bb075'::uuid, 'As crianças ___ no parque todos os dias.', 'brincam', 'concordancia-verbal', 'B1', '["brincaram", "brincava", "brincam", "brinca"]', 'A resposta correta é "brincam". O verbo deve concordar com o sujeito em número e pessoa. Atenção a sujeitos compostos e expressões partitivas. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "brincam". The verb must agree with the subject in number and person. Watch for compound subjects and partitive expressions. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "brincam". Дієслово має узгоджуватися з підметом за числом і особою. Звертайте увагу на складені підмети та партитивні вирази. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'brincar', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('7ffbb794-2356-41b3-8a97-b69f493965bf'::uuid, '___ alguém que fale chinês nesta empresa?', 'Conheces', 'saber-conhecer', 'B1', '["Conheces", "Sabes", "Tens", "Podes"]', '''Conhecer'' refere-se a estar familiarizado com pessoas.', '''Conhecer'' refers to being acquainted with people.', '''Conhecer'' означає бути знайомим з людьми.', 'saber / conhecer', '0.5', '57', '2025-07-26 09:07:03.293997+01', '2025-08-11 17:01:42.904008+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0f6a9ae9-b478-48f2-be76-4821b942720d'::uuid, 'Espero que ___ resolver o problema sozinha.', 'consigas', 'verbos-opiniao-sentimento', 'B1', '["consigas", "consegues", "conseguires", "conseguir"]', 'Após ''espero que'', usamos o presente do conjuntivo para expressar desejo ou esperança.', 'After ''espero que'' (I hope that), we use the present subjunctive to express wish or hope.', 'Після ''espero que'' (я сподіваюся, що) ми використовуємо теперішній час суб''єктивного способу для вираження бажання або надії.', 'conseguir (tu)', '0.5', '0', '2025-07-26 09:05:31.143601+01', '2025-08-10 12:19:43.605807+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('79659b3f-eb96-4dad-bb09-726ecd924490'::uuid, 'Se eu ___ mais tempo, teria ido visitar-te.', 'tivesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["tinha", "tivesse", "tiver", "tenha"]', 'Em condições irreais no passado, usamos ''se'' + imperfeito do conjuntivo na oração condicional.', 'For unreal conditions in the past, we use ''se'' + imperfect subjunctive in the conditional clause.', 'Для нереальних умов у минулому ми використовуємо ''se'' + імперфект суб''юнктива в умовному реченні.', NULL, '0.5', '56', '2025-07-25 13:26:00.386049+01', '2025-08-11 17:01:27.195367+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('63abfc90-be51-4f46-a015-4abb6065d9c8'::uuid, 'Tomara que eles ___ a verdade.', 'digam', 'presente-conjuntivo-irregulares', 'B1', '["diriam", "digam", "dizem", "disseram"]', 'A resposta correta é "digam". Verbos irregulares no presente do conjuntivo: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "digam". Irregular verbs in the present subjunctive: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "digam". Неправильні дієслова в теперішньому кон’юнктиві: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'dizer', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:32.165324+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('7aeee8e6-0c6e-4f37-8b0e-089cd4089ac5'::uuid, 'O livro ___ foi escrito por José Saramago ganhou o Prémio Nobel.', 'que', 'pronomes-relativos', 'B1', '["que", "onde", "qual", "cujo"]', 'Usamos ''que'' como pronome relativo para referir ao sujeito da oração anterior.', 'We use ''que'' as a relative pronoun to refer to the subject of the previous clause.', 'Ми використовуємо ''que'' як відносний займенник для посилання на підмет попереднього речення.', NULL, '0.5', '0', '2025-07-25 13:25:43.08235+01', '2025-07-26 16:16:11.022946+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('8fccc998-6de6-4c88-9b20-1a950905484d'::uuid, 'Espero que ___ um bom fim de semana em Lisboa.', 'tenhas', 'presente-conjuntivo-irregulares', 'B2', '["tens", "teres", "tenhas", "tivesses"]', 'Após ''espero que'' usa-se o presente do conjuntivo para expressar desejo ou esperança.', 'After ''espero que'' we use the present subjunctive to express hope or desire.', 'Після ''espero que'' використовуємо теперішній час суб''юнктиву для вираження надії чи бажання.', 'ter (tu)', '0.5', '57', '2025-07-26 16:16:17.824+01', '2025-08-11 17:01:41.690705+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4ea81fc1-d216-438b-bf09-41df8a8b2111'::uuid, 'O relatório ___ ser entregue até amanhã ao meio-dia.', 'tem de', 'dever-ter-de', 'B1', '["tem de", "deve", "teria de", "há de"]', '''Ter de'' expressa uma obrigação mais forte e inevitável que ''dever''.', '''Ter de'' expresses a stronger, non-negotiable obligation compared to ''dever''.', '''Ter de'' виражає сильніше, безумовне зобов''язання порівняно з ''dever''.', 'dever / ter de', '0.5', '57', '2025-07-26 16:16:17.822827+01', '2025-08-11 17:01:42.898415+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('abe5d7ad-a6dc-4654-b46e-398701132c83'::uuid, 'Embora ele ___ que está errado, continua a insistir.', 'saiba', 'presente-conjuntivo-irregulares', 'B2', '["sabe", "soubesse", "saiba", "conheça"]', 'Após ''embora'', usamos o presente do conjuntivo para expressar uma concessão no presente.', 'After ''embora'' (although), we use the present subjunctive to express a present concession.', 'Після ''embora'' (хоча) ми використовуємо теперішній час суб''єктивного способу для вираження поступки в теперішньому часі.', 'saber', '0.5', '57', '2025-07-26 09:05:34.544796+01', '2025-08-11 17:01:44.370297+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e07c4acb-1c4a-4e54-81d3-967b1d809890'::uuid, 'Quando ___ o trabalho, avisa-me.', 'acabares', 'futuro-conjuntivo-conjuncoes', 'B2', '["acabaste", "acabarás", "acabas", "acabares"]', 'Com ''quando'' referindo-se ao futuro, usamos o futuro do conjuntivo.', 'With ''quando'' referring to the future, we use future subjunctive.', 'З ''quando'', що стосується майбутнього, використовуємо майбутній час суб''юнктива.', 'acabar (tu)', '0.5', '57', '2025-07-26 09:05:34.551051+01', '2025-08-11 17:01:44.374788+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('01433749-3489-48dd-ab64-0db2d6fb2ef2'::uuid, 'Tu ___ muito café.', 'tens', 'verbo-ter', 'A1', '["tens", "tem", "temos"]', 'Com o pronome ''tu'', usamos ''tens'' no presente do indicativo.', 'With the pronoun ''you'' (informal), we use ''tens'' in the present indicative.', 'З займенником ''ти'' використовуємо ''tens'' в теперішньому часі.', 'ter', '0.5', '0', '2025-07-25 15:50:29.242815+01', '2025-08-10 21:24:24.119087+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('1a2d9f52-2e19-4a75-ac66-620bf3847c2a'::uuid, 'Embora ___ tarde, continuámos a conversar.', 'fosse', 'conjuntivo-geral', 'B2', '["seria", "fosse", "era", "é"]', 'Com ''embora'' usa-se o conjuntivo, exprimindo concessão. No passado/descritivo, usa-se o imperfeito do conjuntivo: ''fosse''.', 'With ''embora'' the subjunctive is required to express concession. In past narrative, the imperfect subjunctive ''fosse'' fits.', 'Після ''embora'' потрібен кон''юнктив для поступки. У минулому вживається імперфект кон''юнктива: ''fosse''.', '(3ª sing.)', '0.6', '0', '2025-08-10 20:28:53.053116+01', '2025-08-10 20:28:53.053116+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ed50a75f-fbe7-456f-b0d5-71ea2496f13c'::uuid, 'Quando ___ o trabalho, poderemos ir de férias.', 'acabarmos', 'futuro-conjuntivo-conjuncoes', 'B2', '["acabamos", "acabarmos", "acabemos", "acabar"]', 'Após ''quando'' referindo-se ao futuro, usamos o futuro do conjuntivo.', 'After ''quando'' referring to the future, we use the future subjunctive.', 'Після ''quando'', що відноситься до майбутнього, ми використовуємо майбутній час суб''юнктива.', 'acabar', '0.5', '56', '2025-07-25 13:26:00.388802+01', '2025-08-11 17:01:26.941427+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e31a3167-87f8-4ad2-a739-ac5c9359bafe'::uuid, 'Não ___ trabalhar hoje porque estou doente.', 'consigo', 'poder-conseguir', 'B1', '["consigo", "posso", "devo", "sei"]', 'Usamos ''conseguir'' para expressar capacidade física ou mental específica, não permissão.', 'We use ''conseguir'' to express specific physical or mental ability, not permission.', 'Ми використовуємо ''conseguir'' для вираження конкретної фізичної або розумової здатності, а не дозволу.', 'poder / conseguir', '0.5', '57', '2025-07-26 09:05:31.781138+01', '2025-08-11 17:01:44.372481+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('fd4b9622-f115-40a0-be34-d442a81fdbfa'::uuid, 'Se ___ mais cedo, teríamos apanhado o comboio.', 'tivéssemos saído', 'se-preterito-imperfeito-conjuntivo', 'B2', '["tínhamos saído", "tivéssemos saído", "saíssemos", "teríamos saído"]', 'Em condições irreais no passado, usamos o mais-que-perfeito do conjuntivo na condição.', 'For unreal past conditions, we use the pluperfect subjunctive in the if-clause.', 'Для нереальних умов у минулому використовуємо давноминулий час суб''юнктиву в умовному реченні.', 'ter + saído (nós)', '0.5', '57', '2025-07-26 09:05:31.143084+01', '2025-08-11 17:01:46.25763+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0a824625-0ca6-496b-a512-f7005f46a903'::uuid, 'Assim que ___ o trabalho, posso ir ter contigo.', 'conseguir', 'futuro-conjuntivo-conjuncoes', 'B2', '["consiga", "conseguir", "consigo"]', 'Com ''assim que'' referindo-se ao futuro, usamos o futuro do conjuntivo.', 'With ''assim que'' (as soon as) referring to the future, we use the future subjunctive.', 'З ''assim que'' (як тільки), що відноситься до майбутнього, використовуємо майбутній час суб''єктивного способу.', 'conseguir', '0.5', '57', '2025-07-25 15:59:24.771313+01', '2025-08-11 17:01:47.65861+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('468c7407-d2e4-42d6-9386-8a66065209a3'::uuid, 'Quem quer que ___ este documento deve assiná-lo.', 'receba', 'oracoes-relativas-conjuntivo', 'B2', '["receberá", "receba", "recebe", "receber"]', 'Após ''quem quer que'', usamos o presente do conjuntivo para expressar indefinição.', 'After ''quem quer que'', we use the present subjunctive to express indefiniteness.', 'Після ''quem quer que'' ми використовуємо теперішній час суб''юнктива для вираження невизначеності.', 'receber', '0.5', '0', '2025-07-25 13:26:00.387264+01', '2025-08-10 12:25:39.423318+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('98b20f1f-4f01-4faf-bc1d-1125b97a40fb'::uuid, 'Nós ___ a estudar português.', 'estamos', 'presente-continuo', 'A1', '["estamos", "somos", "estão"]', 'O presente contínuo forma-se com o verbo ''estar'' + a + infinitivo.', 'The present continuous is formed with ''estar'' + a + infinitive.', 'Теперішній тривалий час утворюється за допомогою ''estar'' + a + інфінітив.', 'estar', '0.5', '0', '2025-07-25 15:50:27.210145+01', '2025-08-10 12:44:06.492381+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('8eaf1e89-8000-4d3c-b8ca-1f9865ac725e'::uuid, 'Tu ___ dois irmãos.', 'tens', 'verbo-ter', 'A1', '["tem", "tems", "tens", "tém"]', 'Com ''tu'', a forma correta do verbo ''ter'' é ''tens''.', 'With ''tu'' (you informal), the correct form of the verb ''ter'' is ''tens''.', 'З займенником ''tu'' (ти) правильна форма дієслова ''ter'' - ''tens''.', 'ter', '0.5', '0', '2025-07-25 15:50:29.28672+01', '2025-08-10 21:24:24.116249+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ccef9f8f-395f-40df-b520-7f6e06e69177'::uuid, 'Quando eles chegarem, nós já ___ tudo pronto.', 'teremos deixado', 'futuro-perfeito-indicativo', 'B2', '["teríamos deixado", "deixaremos", "teremos deixado", "temos deixado"]', 'Anterioridade relativamente a ''chegarem'' pede futuro perfeito: ''teremos deixado''.', 'Being prior to ''chegarem'' requires the future perfect: ''teremos deixado''.', 'Попередність щодо ''chegarem'' вимагає майбутнього перфекта: ''teremos deixado''.', NULL, '0.6', '0', '2025-08-10 20:28:53.078997+01', '2025-08-10 20:28:53.078997+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6279c0f4-c4ef-40ce-959b-3adfb4e8d5c2'::uuid, 'Mesmo cansado, ele ___ terminar a maratona.', 'conseguiu', 'poder-conseguir', 'B1', '["poderá", "conseguiu", "conseguia", "pôde"]', '''Conseguir'' mostra que ele alcançou o objetivo apesar do cansaço. É sobre êxito, não permissão.', '''Conseguir'' shows that he achieved the goal despite being tired. It''s about success, not permission.', '''Conseguir'' показує, що він досяг мети, незважаючи на втому. Йдеться про успіх, а не дозвіл.', 'poder / conseguir', '0.6', '57', '2025-08-10 16:07:41.893337+01', '2025-08-11 16:59:42.820007+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('d44f5258-ec02-427f-ae68-7fd256140ebb'::uuid, 'Esperamos que o projeto ___ até ao verão.', 'avance', 'conjuntivo-geral', 'B2', '["avançará", "avança", "avance", "avançou"]', 'Com verbos de esperança no plural com ''que'', o uso do conjuntivo é frequente em português europeu: ''que avance''.', 'With verbs of hope plus ''que'', European Portuguese often uses the subjunctive: ''que avance''.', 'З дієсловами надії та ''que'' у європейській португальській часто вживають кон''юнктив: ''avance''.', NULL, '0.6', '0', '2025-08-10 20:28:53.053116+01', '2025-08-10 20:28:53.053116+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('40693705-be47-4818-888c-a8e353160b9a'::uuid, 'Não ___ abrir a porta porque perdi as chaves.', 'consigo', 'poder-conseguir', 'B1', '["consego", "posso", "podo", "consigo"]', 'Usamos ''conseguir'' quando se trata de uma impossibilidade física ou prática específica (neste caso, a impossibilidade física de abrir a porta por falta das chaves), enquanto ''poder'' seria mais adequado para permissão ou possibilidade geral.', 'We use ''conseguir'' when referring to a specific physical inability or practical impossibility (in this case, the physical impossibility of opening the door due to lost keys), while ''poder'' would be more suitable for permission or general possibility.', 'Ми використовуємо ''conseguir'', коли йдеться про конкретну фізичну неможливість або практичну нездатність (у цьому випадку, фізична неможливість відчинити двері через втрату ключів), тоді як ''poder'' більше підходить для дозволу чи загальної можливості.', 'conseguir', '0.5', '57', '2025-08-10 16:20:36.012694+01', '2025-08-11 16:59:42.824816+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('cbbb2350-5e84-4434-aa76-d32fb214d7b3'::uuid, 'Ele ___ terminar o projeto antes do prazo, apesar das dificuldades.', 'conseguiu', 'poder-conseguir', 'B1', '["poderá", "pôde", "conseguiu", "conseguia"]', 'Usamos ''conseguir'' para indicar que a pessoa foi capaz de realizar algo com esforço. ''Poder'' indica permissão ou possibilidade, enquanto ''conseguir'' foca no êxito da ação.', '''Conseguir'' is used to indicate that someone was able to accomplish something, often with effort. ''Poder'' indicates permission or possibility, whereas ''conseguir'' emphasizes successful completion.', 'Дієслово ''conseguir'' вживається, коли йдеться про те, що хтось зміг досягти чогось, часто доклавши зусиль. ''Poder'' означає дозвіл або можливість, тоді як ''conseguir'' підкреслює успішне виконання.', 'poder / conseguir', '0.6', '57', '2025-08-10 16:07:41.88558+01', '2025-08-11 17:01:41.68995+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('355f980a-00c3-4b9c-9ddd-287880488dfb'::uuid, 'Não ___ terminar o trabalho ontem porque estava doente.', 'consegui', 'poder-conseguir', 'B1', '["podia", "pude", "consegui"]', '''Conseguir'' indica capacidade física ou logística de realizar algo, enquanto ''poder'' indica permissão ou possibilidade.', '''Conseguir'' indicates physical or logistical ability to do something, while ''poder'' indicates permission or possibility.', '''Conseguir'' вказує на фізичну або логістичну здатність щось зробити, тоді як ''poder'' вказує на дозвіл або можливість.', 'poder / conseguir', '0.5', '57', '2025-07-26 16:16:11.020842+01', '2025-08-11 17:01:42.901507+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('53f71d65-28cb-4bfd-8f9a-c2bae632b20e'::uuid, 'Enquanto ___ luz natural, aproveitem para fotografar.', 'houver', 'futuro-conjuntivo-conjuncoes', 'B2', '["há", "houver", "houve", "houverá"]', 'Com ''enquanto'' futuro, usa-se ''houver'' no futuro do conjuntivo.', 'With ''enquanto'' about the future, use ''houver'' in the future subjunctive.', 'Зі ''enquanto'' про майбутнє вживають ''houver'' у майбутньому кон''юнктиві.', 'haver', '0.6', '57', '2025-08-10 20:28:53.067197+01', '2025-08-11 17:01:37.441326+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b49e0709-db3a-4d56-997a-3cd68acd4dc6'::uuid, 'Mal ___ a luz, vamos sair com cuidado.', 'apagar', 'futuro-conjuntivo-conjuncoes', 'B2', '["apagar", "apagar", "apagar", "apagar"]', 'Estrutura fixa: ''mal'' + futuro do conjuntivo. 1.ª pessoa plural tem forma idêntica ao infinitivo: ''mal apagar''.', 'Fixed structure: ''mal'' + future subjunctive. Some forms equal the infinitive: ''mal apagar''.', 'Фіксована конструкція: ''mal'' + майбутній кон''юнктив. Форма може збігатися з інфінітивом: ''apagar''.', NULL, '0.6', '57', '2025-08-10 20:28:53.067197+01', '2025-08-11 17:01:38.85274+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('59a0b864-7e85-402c-baaa-e6cd1ba3ff4f'::uuid, 'Depois que ___ as autorizações, iniciamos as obras.', 'chegarem', 'futuro-conjuntivo-conjuncoes', 'B2', '["chegarão", "cheguem", "chegarem", "chegam"]', 'Em português europeu também ocorre ''depois que'' com valor futuro, pedindo futuro do conjuntivo: ''chegarem''.', 'European Portuguese also uses ''depois que'' with future value, requiring the future subjunctive: ''chegarem''.', 'У європейській португальській ''depois que'' з майбутнім значенням уживає майбутній кон''юнктив: ''chegarem''.', NULL, '0.6', '57', '2025-08-10 20:28:53.067197+01', '2025-08-11 17:01:38.85382+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('f6a373c5-c1de-4d2a-9fff-a4db4506ae20'::uuid, 'Sempre que ___ dúvidas, fala comigo.', 'tiveres', 'futuro-conjuntivo-conjuncoes', 'B2', '["tens", "tenhas", "tiveres", "terás"]', 'Rotina futura/condição com ''sempre que'' pede futuro do conjuntivo: ''tiveres''.', 'Future-leaning routine with ''sempre que'' takes the future subjunctive: ''tiveres''.', 'У значенні майбутньої умови з ''sempre que'' — майбутній кон''юнктив: ''tiveres''.', NULL, '0.6', '57', '2025-08-10 20:28:53.067197+01', '2025-08-11 16:59:42.824801+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('23ee0ec5-fc8a-4011-9263-35d44c1e4467'::uuid, 'Duvido que ele ___ conseguido terminar o projeto a tempo.', 'tenha', 'preterito-perfeito-composto-conjuntivo', 'B2', '["tenha", "teve", "tivesse", "tem"]', 'Após ''duvidar que'', usamos o pretérito perfeito composto do conjuntivo.', 'After ''duvidar que'', we use the present perfect subjunctive.', 'Після ''duvidar que'' використовуємо складений перфект кон''юнктива.', 'ter', '0.5', '0', '2025-07-26 09:05:31.784654+01', '2025-08-10 12:19:39.246427+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('cd051085-79b5-4a32-9c3e-cd9373c9bb25'::uuid, 'Se tu ___ dúvidas, liga-me sem hesitar.', 'tiveres', 'se-futuro-conjuntivo', 'B2', '["tiveres", "tens", "terás", "tenhas"]', 'Para ''tu'', usa-se ''tiveres'' no futuro do conjuntivo.', 'For ''tu'', use ''tiveres'' in the future subjunctive.', 'Для ''ти'' потрібне ''tiveres'' у майбутньому кон''юнктиві.', NULL, '0.6', '0', '2025-08-10 20:28:53.071357+01', '2025-08-10 20:28:53.071357+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('a96ce2d0-3040-4d42-b201-949c997b29e8'::uuid, 'Não creio que eles ___ muito tempo para praticar.', 'tenham tido', 'preterito-perfeito-composto-conjuntivo', 'B2', '["tiveram tido", "tenham ter", "tenham tido", "têm tido"]', 'Negação de crença + PPC do conjuntivo: ''tenham tido''.', 'Negative belief + compound perfect subjunctive: ''tenham tido''.', 'Заперечення віри + складений перфект кон''юнктива: ''tenham tido''.', NULL, '0.6', '0', '2025-08-10 20:28:53.071357+01', '2025-08-10 20:28:53.071357+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3c9e6bd4-a28a-43e4-985d-036bf6746cf2'::uuid, 'É bom que os alunos ___ as leituras semanais.', 'tenham feito', 'preterito-perfeito-composto-conjuntivo', 'B2', '["tenham fazer", "tenham feito", "tiveram feito", "têm feito"]', 'Avaliação presente de hábito recente: PPC do conjuntivo ''tenham feito''.', 'Present evaluation of recent habit: compound perfect subjunctive ''tenham feito''.', 'Оцінка теперішнього щодо нещодавньої звички: ''tenham feito''.', NULL, '0.6', '0', '2025-08-10 20:28:53.071357+01', '2025-08-10 20:28:53.071357+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('579c64a4-b7b9-4734-a665-e97204ab4329'::uuid, 'Se tu ___ mais cedo, apanhavas o comboio.', 'tivesses chegado', 'mais-que-perfeito-composto-conjuntivo', 'B2', '["tivesses chegado", "chegaste", "chegavas", "chegarias"]', 'Para ''tu'', usa-se ''tivesses + particípio'' na condição contrafactual.', 'For ''tu'', use ''tivesses + participle'' in counterfactual condition.', 'Для ''ти'' — ''tivesses + дієприкметник'' у контрфактичній умові.', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('f6d19bd9-ad86-4a64-97ea-bf9ac786a25e'::uuid, 'Se eu te ___ ontem, combinávamos logo.', 'tivesse encontrado', 'mais-que-perfeito-composto-conjuntivo', 'B2', '["encontrava", "encontraria", "encontrei", "tivesse encontrado"]', 'Para passado não realizado, usa-se ''tivesse + particípio''.', 'For unreal past, use ''tivesse + participle''.', 'Для нереального минулого — ''tivesse + дієприкметник''.', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e64753d3-c113-474d-a7d3-9e93efd03666'::uuid, 'Se a equipa ___ o plano, evitaria erros.', 'tivesse seguido', 'mais-que-perfeito-composto-conjuntivo', 'B2', '["tivesse seguido", "seguiria", "seguiu", "seguia"]', 'Estrutura típica: ''se'' + MqP composto do conjuntivo (''tivesse seguido'').', 'Typical structure: ''se'' + compound pluperfect subjunctive (''tivesse seguido'').', 'Типова структура: ''se'' + складений плюсквамперфект кон''юнктива (''tivesse seguido'').', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('550a78b3-41ea-4d5e-8417-ec9d0e2c8fa9'::uuid, 'Tomara que tu me ___ hoje mesmo!', 'ligues', 'frases-exclamativas-desejo', 'B2', '["ligas", "ligasses", "ligues", "ligarás"]', 'Com ''tomara que'', usa-se presente do conjuntivo: ''ligues''.', 'With ''tomara que'', use the present subjunctive: ''ligues''.', 'З ''tomara que'' потрібен презент кон''юнктива: ''ligues''.', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('66ea881b-e13a-47b8-b22a-50dad0e6c740'::uuid, 'Amanhã nós ___ viajar.', 'vamos', 'futuro-imperfeito', 'A2', '["vamos", "o", "em", "de"]', 'Usamos "vamos" (future with ir + infinitive).', 'We use "vamos" (future with ir + infinitive).', 'Ми використовуємо "vamos" (future with ir + infinitive).', 'ir', '0.5', '0', '2025-07-19 14:59:16.062684+01', '2025-08-10 12:45:45.203156+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e28e261f-854d-42d4-b8a6-91af2045eeee'::uuid, 'Ele ___ a estudar português.', 'está', 'presente-continuo', 'A1', '["estou", "estás", "está"]', 'No presente contínuo, usamos ''está'' (3ª pessoa) + a + infinitivo.', 'In present continuous, we use ''está'' (3rd person) + a + infinitive.', 'У теперішньому тривалому часі ми використовуємо ''está'' (3-тя особа) + a + інфінітив.', 'estar', '0.5', '0', '2025-07-25 15:50:31.618034+01', '2025-08-10 21:24:18.53699+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('78840566-1b69-42dd-a70f-d3cf8f536af3'::uuid, 'Vocês ___ falar com a diretora agora.', 'podem', 'poder-conseguir', 'B1', '["conseguem", "podia", "poder", "podem"]', 'A resposta correta é "podem". Usa-se "poder" para permissão/possibilidade e "conseguir" para capacidade/resultado alcançado. No presente, a forma deve concordar com o sujeito. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "podem". Use "poder" for permission/possibility and "conseguir" for ability/result achieved. In the present tense, the form must agree with the subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "podem". "poder" вживається для дозволу/можливості, а "conseguir" — для спроможності/результату. У теперішньому часі форма має узгоджуватися з підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'poder / conseguir', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:32.165908+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('783acb00-28b9-42cf-9ea4-5e24e6bff4a7'::uuid, 'O relatório ___ entregue ao diretor ontem.', 'foi', 'voz-passiva', 'B1', '["estava", "seria", "foi", "tinha"]', 'Na voz passiva no pretérito perfeito, usamos ''foi'' + particípio passado.', 'In passive voice in simple past, we use ''foi'' + past participle.', 'У пасивному стані в минулому часі використовуємо ''foi'' + дієприкметник минулого часу.', NULL, '0.5', '57', '2025-07-26 09:07:03.292444+01', '2025-08-11 17:01:42.90285+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('cbc0c196-b8f8-45ff-8d0f-caf2ee8023b9'::uuid, 'Eles ___ as mãos antes de comer.', 'lavam-se', 'pronomes-reflexos', 'A1', '["lavam-se", "se-lavam", "lavam"]', 'Com verbos reflexivos, o pronome ''-se'' junta-se ao verbo na forma correta.', 'With reflexive verbs, the pronoun ''-se'' is attached to the verb in the correct form.', 'З рефлексивними дієсловами займенник ''-se'' приєднується до дієслова у правильній формі.', 'lavar-se', '0.5', '0', '2025-07-25 13:22:48.088717+01', '2025-08-10 12:44:28.298147+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('d1eb907e-c653-4561-be5c-277dacf2de50'::uuid, '___ quer que seja, não pode entrar sem identificação.', 'Quem', 'pronomes-indefinidos', 'B1', '["Quem", "Qual", "Que", "Cujo"]', '''Quem quer que'' é uma expressão indefinida que significa ''qualquer pessoa que''.', '''Quem quer que'' is an indefinite expression meaning ''whoever''.', '''Quem quer que'' - це неозначений вираз, що означає ''хто б не''.', NULL, '0.5', '0', '2025-07-26 09:05:31.785242+01', '2025-08-10 12:19:39.240848+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9536c3f0-037d-4256-abbc-27b67b06a5a5'::uuid, 'Quem quer que ___ na reunião deve avisar com antecedência.', 'esteja', 'presente-conjuntivo-irregulares', 'B2', '["estivesse", "estiver", "está", "esteja"]', 'Após ''quem quer que'', usamos o presente do conjuntivo.', 'After ''quem quer que'' (whoever), we use present subjunctive.', 'Після ''quem quer que'' (хто б не) використовуємо теперішній час кон''юнктива.', 'estar (ele/ela)', '0.5', '56', '2025-07-25 13:51:24.440649+01', '2025-08-11 17:01:26.945114+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('d5dd18b3-acfa-4dba-bec4-293bd957243a'::uuid, 'Olha ___ foto antiga na parede distante.', 'aquela', 'pronomes-demonstrativos', 'B1', '["esta", "essa", "aquela", "aquilo"]', 'A resposta correta é "aquela". Este/esta/isto referem-se a algo perto do falante; esse/essa/isso a algo perto do ouvinte; aquele/aquela/aquilo a algo distante de ambos. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "aquela". Este/esta/isto refer to something near the speaker; esse/essa/isso to something near the listener; aquele/aquela/aquilo to something far from both. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "aquela". Este/esta/isto — близько до мовця; esse/essa/isso — близько до слухача; aquele/aquela/aquilo — далеко від обох. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'longe de ambos', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('77dd2e83-c46e-48cd-b2c4-437210e0d990'::uuid, '___ livros são interessantes.', 'Estes', 'demonstrativos-variaveis', 'A1', '["Aqueles", "Essa", "Este", "Estes"]', 'Usamos ''estes'' para objetos plurais masculinos próximos do falante.', 'We use ''estes'' for plural masculine objects close to the speaker.', 'Використовуємо ''estes'' для множини чоловічого роду, що знаходяться поруч з мовцем.', NULL, '0.5', '0', '2025-07-25 15:50:29.241703+01', '2025-08-10 12:45:45.20358+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('a5c46120-7788-42f2-b679-d3b1c7df2065'::uuid, 'Quero ___ ver amanhã cedo.', 'te', 'colocacao-pronominal', 'B1', '["se", "me", "o", "te"]', 'A resposta correta é "te". Em Portugal, a ênclise é frequente com verbos no infinitivo/imperativo afirmativo; a próclise ocorre com palavras atrativas (não, que, se, advérbios, pronomes relativos). O pronome deve ocupar a posição correta segundo a regra. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "te". In European Portuguese, enclisis is common with infinitives/affirmative imperatives; proclisis occurs with attractors (não, que, se, adverbs, relative pronouns). Place the clitic pronoun according to the rule. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "te". В європейській португальській енкліза вживається з інфінітивом/стверджувальним наказовим, прокліза — з атракторами (não, que, se, прислівниками, відносними займенниками). Займенник має стояти у правильній позиції. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'ênclise com infinitivo', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ebb712fc-7c4b-40d8-8ab1-109bc1187378'::uuid, '___ livros são interessantes?', 'Estes', 'demonstrativos-variaveis', 'A1', '["Essa", "Este", "Estes", "Aqueles"]', '''Estes'' é o demonstrativo masculino plural para objetos próximos do falante.', '''Estes'' is the masculine plural demonstrative for objects close to the speaker.', '''Estes'' - це вказівний займенник чоловічого роду множини для об''єктів, близьких до мовця.', NULL, '0.5', '0', '2025-07-25 13:22:48.089139+01', '2025-08-10 12:44:06.491993+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3a4c4848-7d4a-4bc3-866c-5ba572104144'::uuid, 'A carta ___ ontem pelo carteiro.', 'foi entregue', 'voz-passiva', 'B1', '["estava entregue", "foi entregue", "tinha entregue"]', 'Na voz passiva, usamos ''ser'' + particípio passado para ações concluídas.', 'In passive voice, we use ''ser'' + past participle for completed actions.', 'У пасивному стані ми використовуємо ''ser'' + дієприкметник минулого часу для завершених дій.', 'entregar', '0.5', '56', '2025-07-25 13:25:45.485838+01', '2025-08-11 17:01:27.197616+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c40536c1-6605-4fc5-b37d-e1b7fffd4df6'::uuid, 'Vês ___ cadeira ao teu lado?', 'essa', 'pronomes-demonstrativos', 'B1', '["isso", "aquela", "essa", "esta"]', 'A resposta correta é "essa". Este/esta/isto referem-se a algo perto do falante; esse/essa/isso a algo perto do ouvinte; aquele/aquela/aquilo a algo distante de ambos. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "essa". Este/esta/isto refer to something near the speaker; esse/essa/isso to something near the listener; aquele/aquela/aquilo to something far from both. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "essa". Este/esta/isto — близько до мовця; esse/essa/isso — близько до слухача; aquele/aquela/aquilo — далеко від обох. Ця форма відповідає контексту і зберігає граматичну правильність речення.', '', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('113c6b08-06d2-48f8-8a96-5d0c5b77e49d'::uuid, 'O que é ___ no chão ao pé de ti?', 'isso', 'pronomes-demonstrativos', 'B1', '["isso", "isto", "esse", "aquilo"]', 'A resposta correta é "isso". Este/esta/isto referem-se a algo perto do falante; esse/essa/isso a algo perto do ouvinte; aquele/aquela/aquilo a algo distante de ambos. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "isso". Este/esta/isto refer to something near the speaker; esse/essa/isso to something near the listener; aquele/aquela/aquilo to something far from both. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "isso". Este/esta/isto — близько до мовця; esse/essa/isso — близько до слухача; aquele/aquela/aquilo — далеко від обох. Ця форма відповідає контексту і зберігає граматичну правильність речення.', '', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('f325f3c1-5cc4-4aca-be3b-58a1fb872d23'::uuid, 'É o autor ___ livros admiro.', 'cujos', 'pronomes-relativos', 'B1', '["cujos", "quem", "que", "dos quais"]', 'A resposta correta é "cujos". Pronomes relativos como ''que'', ''quem'', ''o qual'', ''cujo'' ligam orações e retoman um antecedente, respeitando concordância e regência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "cujos". Relative pronouns like ''que'', ''quem'', ''o qual'', ''cujo'' link clauses and refer back to an antecedent, observing agreement and government. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "cujos". Відносні займенники ''que'', ''quem'', ''o qual'', ''cujo'' поєднують частини речення і відсилають до антецедента з дотриманням керування і узгодження. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'posse com ''cujo''', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4c868c84-db0d-4264-8c70-b8c39871fb59'::uuid, 'Os amigos ___ falei já chegaram.', 'de quem', 'pronomes-relativos', 'B1', '["de quem", "a quem", "quem", "que"]', 'A resposta correta é "de quem". Pronomes relativos como ''que'', ''quem'', ''o qual'', ''cujo'' ligam orações e retoman um antecedente, respeitando concordância e regência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "de quem". Relative pronouns like ''que'', ''quem'', ''o qual'', ''cujo'' link clauses and refer back to an antecedent, observing agreement and government. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "de quem". Відносні займенники ''que'', ''quem'', ''o qual'', ''cujo'' поєднують частини речення і відсилають до антецедента з дотриманням керування і узгодження. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'regência do verbo falar de', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('015e72d4-35a8-46ba-8c55-e51f043eb701'::uuid, 'Duvido que ele ___ tempo para terminar hoje.', 'tenha', 'conjuntivo-geral', 'B2', '["terá", "tenha", "teve", "tem"]', 'Após verbos de dúvida como ''duvidar'', usa-se o conjuntivo. A forma correta do verbo ''ter'' no conjuntivo presente, 3.ª singular, é ''tenha''.', 'After verbs of doubt like ''duvidar'', Portuguese takes the subjunctive. The correct present subjunctive of ''ter'' (3rd sg) is ''tenha''.', 'Після дієслів сумніву, як-от ''duvidar'', вживають кон''юнктива. Правильна форма ''ter'' — ''tenha''.', NULL, '0.6', '0', '2025-08-10 20:28:53.053116+01', '2025-08-10 20:28:53.053116+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('22e8adc6-8dca-402d-843b-5eabc7c486e7'::uuid, 'A senhora ___ falei é simpática.', 'a quem', 'pronomes-relativos', 'B1', '["com quem", "quem", "a quem", "que"]', 'A resposta correta é "a quem". Pronomes relativos como ''que'', ''quem'', ''o qual'', ''cujo'' ligam orações e retoman um antecedente, respeitando concordância e regência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "a quem". Relative pronouns like ''que'', ''quem'', ''o qual'', ''cujo'' link clauses and refer back to an antecedent, observing agreement and government. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "a quem". Відносні займенники ''que'', ''quem'', ''o qual'', ''cujo'' поєднують частини речення і відсилають до антецедента з дотриманням керування і узгодження. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'regência com ''a''', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0a19a87c-7713-412e-abb0-ce944047c66a'::uuid, 'Tomara que tudo ___ como planeámos.', 'corra', 'conjuntivo-geral', 'B2', '["corra", "correrá", "correu", "corre"]', 'Com ''tomara que'' exprimimos desejo e pedimos o conjuntivo presente: ''corra''.', 'With ''tomara que'' (may/hopefully), we use the present subjunctive: ''corra''.', 'З ''tomara que'' виражаємо побажання, тому потрібен презент кон''юнктива: ''corra''.', 'correr', '0.6', '0', '2025-08-10 20:28:53.053116+01', '2025-08-10 20:28:53.053116+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e3acd1cd-f648-4914-b3eb-9ce0d934b3d9'::uuid, 'Chegámos ___ aeroporto três horas antes.', 'ao', 'contraccoes-preposicoes', 'B1', '["para o", "a o", "ao", "no"]', 'A resposta correta é "ao". Contrações como ''do/da/dos/das'' (de+o/a/os/as), ''ao/aos'' (a+o/os), ''pelo/pela'' (por+o/a) são obrigatórias em português padrão. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "ao". Contractions like ''do/da/dos/das'' (de+o/a/os/as), ''ao/aos'' (a+o/os), ''pelo/pela'' (por+o/a) are mandatory in standard Portuguese. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "ao". Скорочення типу ''do/da/dos/das'' (de+артикль), ''ao/aos'' (a+артикль), ''pelo/pela'' (por+артикль) є обов’язковими в стандартній португальській. Ця форма відповідає контексту і зберігає граматичну правильність речення.', '', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4b6a52b1-7cdd-419e-be2f-ab773962f6c5'::uuid, 'Eu ___ vi ontem.', 'te', 'pronomes-pessoais', 'A2', '["te", "um", "o", "em"]', 'A resposta correta é "te".', 'The correct answer is "te".', 'Правильна відповідь: "te".', NULL, '0.5', '0', '2025-07-19 14:59:16.063119+01', '2025-08-10 12:45:45.206877+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('90dcf6cb-0c83-4b31-9b3b-6f02227489a8'::uuid, 'É provável que a reunião ___ adiada.', 'seja', 'imperfeito-vs-presente-conjuntivo', 'B2', '["seja", "é", "será", "fosse"]', 'Com ''é provável que'' no presente, pede-se presente do conjuntivo: ''seja''.', 'With ''é provável que'' in the present, use the present subjunctive: ''seja''.', 'Зі зворотом ''é provável que'' у теперішньому потрібен презент кон''юнктива: ''seja''.', 'ser', '0.6', '0', '2025-08-10 20:28:53.061343+01', '2025-08-10 20:28:53.061343+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('785b1d2f-6c30-475b-bda9-ef22eec2dc7b'::uuid, 'O relatório apresenta dados ___ e claros.', 'precisos', 'concordancia-nominal', 'B1', '["precisa", "precisos", "precisas", "preciso"]', 'A resposta correta é "precisos". Na concordância nominal, artigos, adjetivos e pronomes devem concordar em género e número com o nome. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "precisos". In nominal agreement, articles, adjectives, and pronouns must agree in gender and number with the noun. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "precisos". За правилом іменної узгодженості артиклі, прикметники та займенники мають узгоджуватися з іменником за родом і числом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'adj. com ''dados'' masc pl', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3f3a2f0c-e3bb-4da7-9c17-0b0b7a75597f'::uuid, 'Se ___ que ela estivesse doente, teria ligado antes.', 'tivesse sabido', 'mais-que-perfeito-composto-conjuntivo', 'B2', '["soubesse", "tivesse sabido", "sabia", "tinha sabido"]', 'O mais-que-perfeito composto do conjuntivo é usado em condições irreais no passado.', 'The compound pluperfect subjunctive is used in unreal conditions in the past.', 'Складений плюсквамперфект умовного способу використовується в нереальних умовах у минулому.', 'saber', '0.5', '0', '2025-07-26 09:05:35.425958+01', '2025-08-10 12:19:39.238716+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('55bc97ff-7b3d-48b7-aa7e-25a4cb92b864'::uuid, 'Daqui a pouco, vocês já ___ tudo o que precisavam.', 'terão resolvido', 'futuro-perfeito-indicativo', 'B2', '["terão resolvido", "têm resolvido", "resolverão", "teriam resolvido"]', 'Para ação concluída num momento futuro próximo, usa-se futuro perfeito: ''terão resolvido''.', 'For an action completed at a near-future point, use the future perfect: ''terão resolvido''.', 'Для дії, завершеної в недалекому майбутньому, вживають майбутній перфект: ''terão resolvido''.', NULL, '0.6', '0', '2025-08-10 20:28:53.078997+01', '2025-08-10 20:28:53.078997+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('afca8465-2629-4651-b8ea-11bb2e833b72'::uuid, 'Gosto que vocês ___ cedo.', 'cheguem', 'verbos-opiniao-sentimento', 'B1', '["chegar", "chegavam", "chegam", "cheguem"]', 'A resposta correta é "cheguem". Verbos de opinião/sentimento como ''achar'', ''gostar'', ''temer'', ''duvidar'' influenciam o modo seguinte: com certeza → indicativo; com dúvida/emoção → conjuntivo. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "cheguem". Opinion/feeling verbs like ''achar'', ''gostar'', ''temer'', ''duvidar'' affect mood: certainty → indicative; doubt/emotion → subjunctive. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "cheguem". Дієслова думки/почуттів, як-от ''achar'', ''gostar'', ''temer'', ''duvidar'', впливають на спосіб: упевненість → індикатив; сумнів/емоція → кон’юнктив. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'apreço → conjuntivo', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('505153dc-0a13-455c-9950-f3c0c1030893'::uuid, 'Ela disse: "Eu estou cansada" → Ela disse que ___ cansada.', 'estava', 'discurso-direto-indireto', 'B1', '["fica", "esteve", "estava", "estaria"]', 'A resposta correta é "estava". No discurso indireto, os tempos e pronomes podem mudar: presente → pretérito imperfeito, futuro → condicional, pronomes e advérbios ajustam-se ao ponto de referência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "estava". In reported speech, tenses and pronouns may shift: present → imperfect past, future → conditional; pronouns/adverbs adjust to the new reference point. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "estava". У непрямій мові часи та займенники можуть змінюватися: теперішній → недоконаний минулий, майбутній → умовний; займенники/прислівники узгоджуються з новою точкою відліку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'estar', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c38900da-c553-4327-a1e4-4d7ac98afef5'::uuid, 'Espero que ele ___ o e-mail.', 'leia', 'verbos-opiniao-sentimento', 'B1', '["lerá", "leia", "lê", "leu"]', 'A resposta correta é "leia". Verbos de opinião/sentimento como ''achar'', ''gostar'', ''temer'', ''duvidar'' influenciam o modo seguinte: com certeza → indicativo; com dúvida/emoção → conjuntivo. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "leia". Opinion/feeling verbs like ''achar'', ''gostar'', ''temer'', ''duvidar'' affect mood: certainty → indicative; doubt/emotion → subjunctive. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "leia". Дієслова думки/почуттів, як-от ''achar'', ''gostar'', ''temer'', ''duvidar'', впливають на спосіб: упевненість → індикатив; сумнів/емоція → кон’юнктив. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'esperança → conjuntivo', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('046ef050-ebe2-4f41-a50f-3e8c628c213c'::uuid, 'Se eu ___ mais dinheiro, compraria uma casa.', 'tivesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["tivesse", "tinha", "tiver", "tenha"]', 'Em condições hipotéticas no presente/futuro, usamos o imperfeito do conjuntivo após ''se''.', 'In hypothetical present/future conditions, we use the imperfect subjunctive after ''se''.', 'У гіпотетичних умовах теперішнього/майбутнього часу ми використовуємо імперфект суб''юнктива після ''se''.', 'ter (eu)', '0.5', '56', '2025-07-25 13:25:45.486937+01', '2025-08-11 17:01:27.19697+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9aa73fa1-314c-4e8a-a8fc-c3ddc8913c2c'::uuid, 'Se eu tivesse tempo, ___ contigo.', 'iria', 'condicional-presente', 'B1', '["iria", "em", "de", "com"]', 'Usamos "iria" (conditional simple).', 'We use "iria" (conditional simple).', 'Ми використовуємо "iria" (conditional simple).', 'ir', '0.5', '0', '2025-07-19 14:59:16.06396+01', '2025-08-10 12:19:31.341002+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('853fe4d9-0ece-4e1e-95c4-dd08a11de053'::uuid, 'Ele contou: "Conheço o gerente" → Ele contou que ___ o gerente.', 'conhecia', 'discurso-direto-indireto', 'B1', '["conheceu", "conhecia", "conheceria", "conhecera"]', 'A resposta correta é "conhecia". No discurso indireto, os tempos e pronomes podem mudar: presente → pretérito imperfeito, futuro → condicional, pronomes e advérbios ajustam-se ao ponto de referência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "conhecia". In reported speech, tenses and pronouns may shift: present → imperfect past, future → conditional; pronouns/adverbs adjust to the new reference point. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "conhecia". У непрямій мові часи та займенники можуть змінюватися: теперішній → недоконаний минулий, майбутній → умовний; займенники/прислівники узгоджуються з новою точкою відліку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'conhecer', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('cba45900-6e01-48af-b4b7-00d3313d7c03'::uuid, 'É necessário que vocês ___ silêncio.', 'façam', 'expressoes-impessoais', 'B1', '["fariam", "fazem", "fazer", "façam"]', 'A resposta correta é "façam". Expressões impessoais como ''é necessário que'', ''é provável que'', ''convém que'' pedem conjuntivo; ''é certo que'' pede indicativo. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "façam". Impersonal expressions like ''é necessário que'', ''é provável que'', ''convém que'' require the subjunctive; ''é certo que'' takes the indicative. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "façam". Безособові вирази на кшталт ''é necessário que'', ''é provável que'', ''convém que'' вимагають кон’юнктива; ''é certo que'' — індикатива. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'fazer', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c32fbeb2-67f3-4974-a872-6d0ae3494b64'::uuid, 'Conheces alguém que ___ o sistema?', 'entenda', 'oracoes-relativas-conjuntivo', 'B1', '["entendeu", "entenderá", "entende", "entenda"]', 'A resposta correta é "entenda". Usa-se o conjuntivo em orações relativas quando há ideia de procura, desejo, indefinição: ''Procuro alguém que saiba'', ''Não há quem faça''. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "entenda". Use the subjunctive in relative clauses expressing search, desire, or non-specificity: ''Procuro alguém que saiba'', ''Não há quem faça''. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "entenda". У відносних підрядних із пошуком/бажанням/невизначеністю використовують кон’юнктив: ''Procuro alguém que saiba'', ''Não há quem faça''. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'entender', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('a8e48b10-fe9a-4505-85af-b81b5c5f38a2'::uuid, 'O relatório ___ entregue ao diretor amanhã.', 'será', 'voz-passiva', 'B1', '["será", "estará", "vai ser", "é"]', 'Na voz passiva com futuro, usamos ''ser'' no futuro + particípio passado.', 'In passive voice for future actions, we use future tense of ''ser'' + past participle.', 'У пасивному стані для майбутніх дій використовуємо майбутній час ''ser'' + дієприкметник минулого часу.', 'entregar', '0.5', '57', '2025-07-25 15:59:25.222237+01', '2025-08-11 17:01:46.258438+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('44ac898d-236c-4e90-a035-7f8725916d31'::uuid, 'Não ___ atender o telefone porque estava ocupado.', 'consegui', 'poder-conseguir', 'B1', '["consigo", "podia", "pude", "consegui"]', 'Usamos ''conseguir'' para expressar impossibilidade devido a circunstâncias específicas ou capacidade física.', 'We use ''conseguir'' to express inability due to specific circumstances or physical capacity.', 'Ми використовуємо ''conseguir'' для вираження неможливості через конкретні обставини або фізичну здатність.', 'poder / conseguir', '0.5', '56', '2025-07-25 13:26:00.378904+01', '2025-08-11 17:01:27.199718+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('38072d26-5603-4b0c-b116-3715caff6632'::uuid, 'Ela não ___ sair ontem porque estava doente.', 'conseguiu', 'poder-conseguir', 'B1', '["conseguiu", "pôde", "podia", "conseguia"]', 'Usamos ''conseguir'' para expressar capacidade física ou circunstancial específica num momento concreto.', 'We use ''conseguir'' to express physical or circumstantial ability at a specific moment.', 'Ми використовуємо ''conseguir'' для вираження фізичної або обставинної здатності в конкретний момент.', 'poder / conseguir', '0.5', '57', '2025-07-25 15:59:25.218171+01', '2025-08-11 17:01:47.661326+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('78f87ae1-cc62-4fb5-9dbd-e18665741b3e'::uuid, 'Quando era criança, ___ muito feliz.', 'era', 'imperfeito-idade-tempo', 'A2', '["era", "o", "com", "um"]', 'Usamos "era" (pretérito imperfeito (imperfect)).', 'We use "era" (pretérito imperfeito (imperfect)).', 'Ми використовуємо "era" (pretérito imperfeito (imperfect)).', 'ser (1ª pessoa)', '0.5', '0', '2025-07-19 14:59:16.062086+01', '2025-08-10 12:45:45.197658+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('f905e893-58a4-4ddd-a720-e7d963354989'::uuid, 'Quando ___ que vais mudar de emprego?', 'soubeste', 'saber-conhecer', 'B1', '["sabes", "soubeste", "conheceste", "conhecias"]', '''Saber'' é usado para informação adquirida ou conhecimento de fatos específicos.', '''Saber'' is used for acquired information or knowledge of specific facts.', '''Saber'' використовується для позначення отриманої інформації або знання конкретних фактів.', 'saber / conhecer', '0.5', '56', '2025-07-25 08:56:04.127517+01', '2025-08-11 17:01:27.824878+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('a852177a-0100-4e92-b72b-905fb8e9ddd5'::uuid, 'O documento ___ entregue ao diretor esta manhã.', 'foi', 'voz-passiva', 'B2', '["estava", "tem", "foi", "seria"]', 'Na voz passiva, usamos o verbo ''ser'' no tempo adequado + particípio passado.', 'In passive voice, we use the verb ''ser'' in the appropriate tense + past participle.', 'У пасивному стані ми використовуємо дієслово ''ser'' у відповідному часі + дієприкметник минулого часу.', NULL, '0.5', '57', '2025-07-26 09:07:02.975883+01', '2025-08-11 17:01:43.836472+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('5d71b742-357d-42e1-89d6-d7eef9cd3d46'::uuid, 'Quando ___ o relatório, enviarei por email.', 'tiver', 'futuro-conjuntivo-conjuncoes', 'B2', '["tenho", "tiver", "tivesse", "ter"]', 'Com ''quando'' referindo-se ao futuro, usamos o futuro do conjuntivo.', 'With ''quando'' referring to the future, we use the future subjunctive.', 'З ''quando'', що стосується майбутнього, використовуємо майбутній час кон''юнктива.', 'ter', '0.5', '57', '2025-07-26 09:05:31.784087+01', '2025-08-11 17:01:44.376073+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('fdfbd045-158f-4c42-926a-29d4465fe17e'::uuid, 'Não ___ abrir a janela porque está emperrada.', 'consigo', 'poder-conseguir', 'B1', '["podo", "consego", "posso", "consigo"]', 'Usamos ''conseguir'' quando se trata de uma incapacidade física específica (a janela está emperrada, impedindo fisicamente a ação), em vez de ''poder'' que seria mais para permissão ou possibilidade geral.', 'We use ''conseguir'' when referring to a specific physical inability (the window is stuck, physically preventing the action), rather than ''poder'' which would be more about permission or general possibility.', 'Ми використовуємо ''conseguir'', коли йдеться про конкретну фізичну неможливість (вікно застрягло, фізично перешкоджаючи дії), а не ''poder'', який більше стосується дозволу чи загальної можливості.', 'poder / conseguir', '0.5', '57', '2025-08-10 16:08:03.204513+01', '2025-08-11 16:59:42.81755+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('157c920e-3bfb-44e0-8192-e2d819e1890a'::uuid, 'O meu carro é ___ pequeno.', 'muito', 'demonstrativos-invariaveis', 'A1', '["muita", "muito", "muitos"]', '''Muito'' é invariável quando usado como advérbio de intensidade.', '''Muito'' is invariable when used as an adverb of intensity.', '''Muito'' є незмінним, коли використовується як прислівник інтенсивності.', NULL, '0.5', '0', '2025-07-25 15:50:31.623136+01', '2025-08-10 21:24:18.504649+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('95c2834b-8d69-4308-adc2-7cc6a64e8797'::uuid, 'O documento ___ enviado pelo correio na semana passada.', 'foi', 'voz-passiva', 'B1', '["foi", "era", "tinha sido", "seria"]', 'Na voz passiva no pretérito perfeito, usamos ''foi'' + particípio passado.', 'In the passive voice in the simple past, we use ''foi'' + past participle.', 'У пасивному стані в минулому часі ми використовуємо ''foi'' + дієприкметник минулого часу.', NULL, '0.5', '57', '2025-07-26 09:05:35.427038+01', '2025-08-11 17:01:43.839205+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b8ed3db1-cc9c-4e83-9b89-a564a064b9a5'::uuid, 'Não ___ dormir ontem por causa do barulho dos vizinhos.', 'consegui', 'poder-conseguir', 'B1', '["consigo", "podia", "consegui", "pude"]', 'Usamos ''conseguir'' quando nos referimos à capacidade física ou mental de realizar algo, especialmente quando há um obstáculo ou dificuldade específica (neste caso, o barulho). ''Poder'' seria mais apropriado para uma permissão ou possibilidade geral.', 'We use ''conseguir'' when referring to the physical or mental ability to accomplish something, especially when there''s a specific obstacle or difficulty (in this case, the noise). ''Poder'' would be more appropriate for general permission or possibility.', 'Ми використовуємо ''conseguir'', коли йдеться про фізичну чи розумову здатність щось зробити, особливо коли є конкретна перешкода чи труднощі (у цьому випадку шум). ''Poder'' більше підходить для загального дозволу чи можливості.', 'poder / conseguir', '0.5', '57', '2025-08-10 16:25:37.922382+01', '2025-08-11 16:59:42.821982+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b62182dd-5dd1-43b6-8d25-35d2cc3b3560'::uuid, 'A Maria está ___ supermercado.', 'no', 'preposicoes', 'A1', '["no", "ao", "na", "em"]', '''No'' é a contração da preposição ''em'' com o artigo ''o''.', '''No'' is the contraction of the preposition ''em'' with the article ''o''.', '''No'' - це скорочення прийменника ''em'' з артиклем ''o''.', NULL, '0.5', '0', '2025-07-25 15:50:30.601453+01', '2025-08-10 21:24:18.535919+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('201a5382-a1f3-46c4-95e9-6443772bb8de'::uuid, 'O João ___ as mãos antes de comer.', 'lava-se', 'pronomes-reflexos', 'A1', '["lava-se", "se lava", "lava"]', 'Com verbos reflexivos, o pronome ''-se'' liga-se ao verbo com hífen.', 'With reflexive verbs, the pronoun ''-se'' is connected to the verb with a hyphen.', 'З рефлексивними дієсловами займенник ''-se'' приєднується до дієслова через дефіс.', 'lavar-se', '0.5', '0', '2025-07-25 15:50:30.595832+01', '2025-08-10 21:24:18.5375+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b7172703-a137-42b1-955c-6aa7cbc419ec'::uuid, 'Eu ___ usar o teu computador um minuto.', 'posso', 'poder-conseguir', 'B1', '["consigo", "poder", "posso", "podia"]', 'A resposta correta é "posso". Usa-se "poder" para permissão/possibilidade e "conseguir" para capacidade/resultado alcançado. No presente, a forma deve concordar com o sujeito. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "posso". Use "poder" for permission/possibility and "conseguir" for ability/result achieved. In the present tense, the form must agree with the subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "posso". "poder" вживається для дозволу/можливості, а "conseguir" — для спроможності/результату. У теперішньому часі форма має узгоджуватися з підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'poder / conseguir', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:32.164748+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('dd98e2c5-cd70-43b7-b54e-5907507ba3cc'::uuid, 'Tenho certeza de que ela ___ resolver o problema sozinha.', 'conseguirá', 'poder-conseguir', 'B1', '["poderá", "conseguirá", "pode"]', 'Usamos ''conseguir'' para expressar capacidade específica de realizar algo.', 'We use ''conseguir'' to express specific ability to accomplish something.', 'Використовуємо ''conseguir'' для вираження конкретної здатності щось виконати.', 'poder / conseguir', '0.5', '57', '2025-07-25 15:59:24.773259+01', '2025-08-11 17:01:47.658904+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3a315e0a-fce8-4d5e-9452-c13dc98e05a2'::uuid, 'A casa ___ construída pelos operários.', 'foi', 'voz-passiva', 'B2', '["foi", "um", "de", "o"]', 'Usamos "foi" (passive voice (simple past)).', 'We use "foi" (passive voice (simple past)).', 'Ми використовуємо "foi" (passive voice (simple past)).', 'construir', '0.5', '57', '2025-07-19 14:59:16.065683+01', '2025-08-11 17:00:02.438219+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('983d501e-676f-488c-ab4a-7a4487f8bf79'::uuid, '___ pessoas ainda não entregaram os documentos.', 'Algumas', 'pronomes-indefinidos', 'B1', '["Nenhumas", "Quaisquer", "Algumas", "Todos"]', '''Algumas'' indica uma quantidade parcial indefinida de pessoas.', '''Algumas'' indicates an indefinite partial quantity of people.', '''Algumas'' вказує на невизначену часткову кількість людей.', NULL, '0.5', '0', '2025-07-26 09:05:34.553462+01', '2025-08-10 12:19:39.24586+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9a0ef98f-8bc8-453c-9193-4a42cb64f43a'::uuid, 'A pessoa ___ livro estou a ler é portuguesa.', 'cujo', 'pronomes-relativos', 'B1', '["cujo", "que", "qual", "onde"]', '''Cujo'' é usado para indicar posse e concorda em género e número com o objeto possuído.', '''Cujo'' is used to indicate possession and agrees in gender and number with the possessed object.', '''Cujo'' використовується для позначення володіння і узгоджується в роді та числі з об''єктом володіння.', NULL, '0.5', '0', '2025-07-26 09:07:02.977336+01', '2025-08-10 12:19:39.241722+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0f4f9594-3b2c-4a48-b1b1-d21b6bb1a346'::uuid, 'O contrato ___ pelas duas partes.', 'foi assinado', 'voz-passiva', 'B1', '["é assinado", "foi assinado", "foi assinadas", "era assinado"]', 'A resposta correta é "foi assinado". A voz passiva analítica forma-se com "ser" + particípio passado, concordando em género e número com o sujeito paciente. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "foi assinado". Analytical passive voice is formed with "ser" + past participle, which agrees in gender and number with the patient subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "foi assinado". Аналітичний пасив утворюється за допомогою "ser" + дієприкметник минулого, який узгоджується за родом і числом із підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'assinar', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:37.443671+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('a4781bd1-f6d6-49ee-a3af-0994884483b9'::uuid, 'É importante que ele ___ a verdade.', 'diga', 'presente-conjuntivo-regulares', 'B2', '["diga", "com", "o", "em"]', 'Usamos "diga" (present subjunctive).', 'We use "diga" (present subjunctive).', 'Ми використовуємо "diga" (present subjunctive).', 'dizer', '0.5', '57', '2025-07-19 14:59:16.066077+01', '2025-08-11 17:00:02.442056+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('f6f632bd-a260-4958-8279-3b8a325e176e'::uuid, 'Se eu ___ que ia chover, teria trazido um guarda-chuva.', 'soubesse', 'se-preterito-imperfeito-conjuntivo', 'B2', '["soubesse", "sabia", "saberia", "soube"]', 'Em condicionais irreais no passado, usamos o imperfeito do conjuntivo após ''se''.', 'In unreal past conditionals, we use the imperfect subjunctive after ''se'' (if).', 'У нереальних умовних реченнях минулого часу ми використовуємо імперфект суб''юнктива після ''se'' (якщо).', 'saber (eu)', '0.5', '57', '2025-07-25 15:59:25.220534+01', '2025-08-11 17:01:47.659838+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('62a64724-9e43-4c27-8ad1-62a6ea1eb7c7'::uuid, 'Quando ___ todo o trabalho, poderemos ir de férias.', 'tivermos terminado', 'futuro-perfeito-conjuntivo', 'B2', '["teremos terminado", "tenhamos terminado", "tivermos terminado"]', 'Com ''quando'' referindo-se ao futuro, usamos o futuro perfeito do conjuntivo.', 'With ''quando'' referring to the future, we use the future perfect subjunctive.', 'З ''quando'', що відноситься до майбутнього, ми використовуємо майбутній доконаний час суб''юнктива.', 'terminar', '0.5', '0', '2025-07-25 13:25:43.08345+01', '2025-08-10 12:25:53.979844+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('226a5e42-8738-4246-a0e2-1c6524305fde'::uuid, '___ é o teu nome?', 'Qual', 'interrogativos', 'A1', '["Quem", "Qual", "Como", "Que"]', '''Qual'' é usado para perguntar o nome em português.', '''Qual'' is used to ask for someone''s name in Portuguese.', '''Qual'' використовується для запитання імені португальською мовою.', NULL, '0.5', '0', '2025-07-25 15:50:29.243239+01', '2025-08-10 12:45:45.205497+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('acb002ce-6597-49fb-9229-5194fb6a1875'::uuid, 'Ela está ___ trabalhar no computador.', 'a', 'presente-continuo', 'A1', '["a", "de", "para"]', 'No presente contínuo, usamos ''estar a'' + infinitivo.', 'In the present continuous, we use ''estar a'' + infinitive.', 'У теперішньому тривалому часі ми використовуємо ''estar a'' + інфінітив.', NULL, '0.5', '0', '2025-07-25 13:22:48.090071+01', '2025-08-10 12:44:06.490558+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e5321016-4e78-424b-8b5c-847d16c4c573'::uuid, '___ livro é interessante.', 'Este', 'demonstrativos-variaveis', 'A1', '["Aquele", "Esse", "Este", "Esta"]', '''Este'' usa-se para objetos próximos do falante.', '''Este'' is used for objects close to the speaker.', '''Este'' використовується для об''єктів, близьких до мовця.', NULL, '0.5', '0', '2025-07-25 15:50:27.212586+01', '2025-08-10 12:44:06.486817+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b72ba086-76d3-4975-9178-fe8c2a2ade86'::uuid, 'Por mais que ___ estudar, não consigo concentrar-me.', 'tente', 'presente-conjuntivo-regulares', 'B1', '["tentasse", "tentar", "tento", "tente"]', 'A expressão ''por mais que'' exige o uso do presente do conjuntivo.', 'The expression ''por mais que'' (no matter how much) requires present subjunctive.', 'Вираз ''por mais que'' (як би не) вимагає теперішнього часу кон''юнктива.', 'tentar (eu)', '0.5', '57', '2025-07-25 13:51:24.442212+01', '2025-08-11 17:01:47.656466+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b498e7f9-4dbc-42d2-af26-24f1e7cbaa84'::uuid, 'Quem quer que ___ essa decisão terá de assumir a responsabilidade.', 'tome', 'presente-conjuntivo-irregulares', 'B2', '["tomar", "tomará", "toma", "tome"]', 'Após ''quem quer que'' usa-se sempre o presente do conjuntivo.', 'After ''quem quer que'' (whoever) we always use the present subjunctive.', 'Після ''quem quer que'' (хто б не) завжди використовуємо теперішній час кон''юнктива.', 'tomar (ele/ela)', '0.5', '57', '2025-07-25 15:59:25.221715+01', '2025-08-11 17:01:46.261048+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('fc3902a5-85ee-4141-9682-54c75e772c6f'::uuid, 'A notícia ___ divulgada pelos meios de comunicação ontem.', 'foi', 'voz-passiva', 'B1', '["era", "seria", "foi", "estava"]', 'Na voz passiva, usamos o verbo ''ser'' no tempo adequado + particípio passado do verbo principal.', 'In passive voice, we use the verb ''ser'' in the appropriate tense + past participle of the main verb.', 'У пасивному стані ми використовуємо дієслово ''ser'' у відповідному часі + дієприкметник минулого часу основного дієслова.', 'divulgar', '0.5', '56', '2025-07-25 13:26:00.384372+01', '2025-08-11 17:01:27.200281+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9fc901d7-00b7-40e0-aa23-1ca0b3ff2a4c'::uuid, 'Não ___ sair hoje porque estou doente.', 'consigo', 'poder-conseguir', 'B1', '["consigo", "posso", "sei"]', '''Conseguir'' indica capacidade física ou habilidade, enquanto ''poder'' indica permissão.', '''Conseguir'' indicates physical ability or capability, while ''poder'' indicates permission.', '''Conseguir'' вказує на фізичну здатність, тоді як ''poder'' вказує на дозвіл.', 'poder / conseguir', '0.5', '56', '2025-07-25 13:25:43.078981+01', '2025-08-11 17:01:27.824263+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('56868fb3-720e-4527-8ebf-8d2b160ca6d3'::uuid, 'Tomara que ela ___ o emprego.', 'consega', 'presente-conjuntivo-regulares', 'B1', '["consegue", "consega", "conseguiu", "conseguiria"]', 'A resposta correta é "consega". O presente do conjuntivo usa-se após expressões de vontade, dúvida, necessidade, entre outras. Regularmente, -ar termina em -e/-es/-e/-emos/-em; -er/-ir em -a/-as/-a/-amos/-am. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "consega". Present subjunctive is used after expressions of wish, doubt, necessity, etc. Regular verbs: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "consega". Теперішній кон’юнктив уживається після виразів бажання, сумніву, необхідності тощо. Регулярні: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'conseguir', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:37.439148+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('2b7a8cfe-a3bf-4fc2-8d84-9940d8de51c8'::uuid, 'Assim que ___ este trabalho, envio-te uma mensagem.', 'terminar', 'futuro-conjuntivo-conjuncoes', 'B2', '["termine", "terminei", "terminasse", "terminar"]', 'Com ''assim que'' referindo-se ao futuro, usamos o futuro do conjuntivo.', 'With ''assim que'' referring to the future, we use the future subjunctive.', 'З ''assim que'', що відноситься до майбутнього, ми використовуємо майбутній час умовного способу.', NULL, '0.5', '57', '2025-07-26 09:05:35.426425+01', '2025-08-11 17:01:43.840292+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0933d63b-e8f4-4e70-967f-89dfa2eef09a'::uuid, 'Espero que vocês ___ um bom fim de semana.', 'tenham', 'presente-conjuntivo-irregulares', 'B2', '["terão", "tenham", "têm", "tiveram"]', 'Após ''espero que'' usamos o presente do conjuntivo.', 'After ''espero que'' (I hope that) we use the present subjunctive.', 'Після ''espero que'' (я сподіваюся, що) використовуємо теперішній час кон''юнктива.', 'ter (eles/elas)', '0.5', '57', '2025-07-25 15:59:25.223819+01', '2025-08-11 17:01:46.263593+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0f1c0764-ffec-491f-beb2-5f7fc6351695'::uuid, 'O livro ___ na semana passada já está esgotado.', 'publicado', 'voz-passiva', 'B1', '["publicado", "publicando", "que publicou"]', 'Na voz passiva adjetiva, usamos o particípio passado como adjetivo.', 'In adjectival passive voice, we use the past participle as an adjective.', 'У пасивному стані прикметника використовуємо дієприкметник минулого часу як прикметник.', 'publicar', '0.5', '57', '2025-07-25 15:59:24.772303+01', '2025-08-11 17:01:47.662047+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ab78dc42-d65a-44d9-ad28-b3be6f2331a3'::uuid, 'Quem me dera que este ano ___ diferente!', 'fosse', 'frases-exclamativas-desejo', 'B2', '["fosse", "é", "será", "seria"]', 'Desejo irreal usa imperfeito do conjuntivo: ''fosse''.', 'An unreal wish uses the imperfect subjunctive: ''fosse''.', 'Нереальне бажання — імперфект кон''юнктива: ''fosse''.', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('0ad61a92-8ddc-4c11-aa74-7f0b96a31bd4'::uuid, 'Oxalá a equipa ___ o campeonato!', 'vença', 'frases-exclamativas-desejo', 'B2', '["vença", "vence", "vencesse", "vencerá"]', 'Desejo provável no presente: presente do conjuntivo ''vença''.', 'Likely wish in the present: present subjunctive ''vença''.', 'Ймовірне побажання теперішнього: презент кон''юнктива ''vença''.', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('4bcba221-7138-446b-a26d-8080e0d874c3'::uuid, 'Quem dera que tudo ___ certo no final!', 'corresse', 'frases-exclamativas-desejo', 'B2', '["correrá", "corre", "correria", "corresse"]', 'Desejo sobre processo futuro irreal: imperfeito do conjuntivo ''corresse''.', 'Wish about an unreal future scenario: imperfect subjunctive ''corresse''.', 'Побажання щодо нереального майбутнього: імперфект кон''юнктива ''corresse''.', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('31323963-f873-49ea-b68d-606d6462c987'::uuid, 'Oxalá não ___ mais cancelamentos este mês!', 'haja', 'frases-exclamativas-desejo', 'B2', '["haverá", "houve", "há", "haja"]', 'Com ''oxalá'' e existência, usa-se conjuntivo presente: ''haja''.', 'With ''oxalá'' and existence, use the present subjunctive: ''haja''.', 'З ''oxalá'' для існування — презент кон''юнктива: ''haja''.', 'haver', '0.6', '0', '2025-08-10 20:28:53.078997+01', '2025-08-10 20:28:53.078997+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b4718544-78a4-4807-b491-cc75a70a294e'::uuid, 'Quem me dera que tudo ___ mais simples!', 'fosse', 'preterito-imperfeito-conjuntivo-exclamativas', 'B2', '["seria", "é", "fosse", "será"]', 'Desejo exclamativo irreal pede imperfeito do conjuntivo: ''fosse''.', 'Unreal wish in exclamation uses the imperfect subjunctive: ''fosse''.', 'Нереальне побажання вимагає імперфекта кон''юнктива: ''fosse''.', NULL, '0.6', '0', '2025-08-10 20:28:53.053116+01', '2025-08-10 20:28:53.053116+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('61c3ffa8-0133-44b9-9ac8-1230132b4428'::uuid, 'Eles só ___ entrar depois que o segurança verificou os documentos.', 'puderam', 'poder-conseguir', 'B1', '["podiam", "conseguiram", "puderam", "conseguiam"]', '''Puderam'' indica que a permissão foi dada após a verificação, e só então foi possível entrar.', '''Puderam'' indicates that permission was given after verification, and only then they could enter.', '''Puderam'' означає, що дозвіл було надано після перевірки, і лише тоді вони змогли увійти.', 'poder / conseguir', '0.6', '57', '2025-08-10 16:07:41.890996+01', '2025-08-11 17:01:41.694355+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('1fe74c96-8b13-4ad6-bd31-63451908ff14'::uuid, 'Quando ___ a verdade, fiquei muito surpreendido.', 'soube', 'saber-conhecer', 'B2', '["soube", "sabia", "conheci", "conhecia"]', 'Usamos ''saber'' quando nos referimos ao momento em que tomamos conhecimento de uma informação.', 'We use ''saber'' when referring to the moment we learn or find out information.', 'Ми використовуємо ''saber'', коли говоримо про момент, коли ми дізнаємося інформацію.', 'saber / conhecer', '0.5', '57', '2025-07-26 09:05:35.425489+01', '2025-08-11 17:01:43.838079+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3ebaa93e-0bbe-4c59-bf7c-840099b654a8'::uuid, 'Não sei como ele ___ resolver aquele problema tão rápido.', 'conseguiu', 'poder-conseguir', 'B1', '["pôde", "conseguiu", "poderá", "conseguia"]', '''Conseguir'' destaca que ele teve êxito na resolução do problema. ''Poder'' indicaria apenas possibilidade, sem garantir sucesso.', '''Conseguir'' emphasizes that he successfully solved the problem. ''Poder'' would only indicate possibility, without guaranteeing success.', '''Conseguir'' підкреслює, що він успішно вирішив проблему. ''Poder'' лише вказував би на можливість, без гарантії успіху.', 'poder / conseguir', '0.6', '57', '2025-08-10 16:07:41.88996+01', '2025-08-11 17:01:41.69134+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('14bc8351-1cd2-4eb0-a8c4-7884a655253e'::uuid, 'Quem dera que eles ___ a nossa proposta!', 'aceitassem', 'preterito-imperfeito-conjuntivo-exclamativas', 'B2', '["aceitam", "aceitariam", "aceitassem", "aceitaram"]', '''Desejo'' em exclamativa requer imperfeito do conjuntivo: ''aceitassem''.', 'Wish in exclamation requires the imperfect subjunctive: ''aceitassem''.', 'Побажання у вигуковій формі вимагає імперфекта кон''юнктива: ''aceitassem''.', NULL, '0.6', '0', '2025-08-10 20:28:53.061343+01', '2025-08-10 20:28:53.061343+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b88ae551-515d-4ede-8663-69e40c579a3c'::uuid, 'Tu ___ uma casa bonita.', 'tens', 'verbo-ter', 'A1', '["tem", "tens", "temos"]', 'Na segunda pessoa do singular, a forma correta do verbo ''ter'' é ''tens''.', 'In second person singular, the correct form of the verb ''ter'' (to have) is ''tens''.', 'У другій особі однини правильна форма дієслова ''ter'' (мати) - ''tens''.', 'ter', '0.5', '0', '2025-07-25 13:22:48.08766+01', '2025-08-10 21:24:24.114223+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('3c4039c3-0f34-4e58-87c2-81a00eded8b0'::uuid, 'A Maria ___ muito feliz hoje.', 'está', 'verbo-estar', 'A1', '["é", "estão", "está", "tem"]', 'Usamos ''está'' (verbo estar) para estados temporários e emoções momentâneas.', 'We use ''está'' (verb estar) for temporary states and momentary emotions.', 'Ми використовуємо ''está'' (дієслово estar) для тимчасових станів та моментних емоцій.', 'estar', '0.5', '0', '2025-07-25 15:50:29.285513+01', '2025-08-10 21:24:24.1225+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ce483541-a95d-42dd-89f6-3e1eaa5fc75a'::uuid, 'Vocês ___ este restaurante desde 2019.', 'conhecem', 'saber-conhecer', 'B1', '["conhecem", "sabem", "conhecer", "conhecia"]', 'A resposta correta é "conhecem". "Saber" indica conhecimento de factos ou habilidade; "conhecer" indica familiaridade com pessoas, lugares ou coisas. O verbo deve concordar com o sujeito no presente. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "conhecem". "Saber" is used for knowledge of facts or skills; "conhecer" is for familiarity with people, places, or things. Use the present form that agrees with the subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "conhecem". "saber" вживається для фактів або умінь; "conhecer" — для знайомства з людьми, місцями чи речами. Потрібна форма теперішнього часу узгоджується з підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'saber / conhecer', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:36.129248+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('345ea809-d18f-45f4-ad8b-c95346851ddd'::uuid, 'Procuro alguém que ___ experiência em auditoria.', 'tenha', 'conjuntivo-geral', 'B2', '["tenha", "tinha", "ter", "tem"]', 'Em orações relativas com valor indefinido/necessidade, usa-se o conjuntivo. ''Alguém que tenha'' indica requisito não garantido.', 'In relative clauses expressing requirement/uncertainty, use the subjunctive. ''Alguém que tenha'' signals a non-guaranteed trait.', 'У відносних реченнях з невизначеністю вживається кон''юнктив. ''Alguém que tenha'' — ознака, якої може й не бути.', NULL, '0.6', '0', '2025-08-10 20:28:53.053116+01', '2025-08-10 20:28:53.053116+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('1a38af37-100d-4e17-82e6-2125d08776b2'::uuid, 'Se os prazos ___ claros, não haveria dúvidas.', 'tivessem sido', 'mais-que-perfeito-composto-conjuntivo', 'B2', '["seriam", "tivessem sido", "eram", "foram"]', 'Passiva com MqP composto do conjuntivo: ''tivessem sido'' + particípio.', 'Passive with compound pluperfect subjunctive: ''tivessem sido'' + participle.', 'Пасив із складеним плюсквамперфектом кон''юнктива: ''tivessem sido''.', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b4c6f45b-1cf7-4402-82e5-14b38bfc31c7'::uuid, 'Se tu ___ a proposta, ganharíamos o cliente.', 'tivesses enviado', 'mais-que-perfeito-composto-conjuntivo', 'B2', '["enviavas", "tivesses enviado", "enviarás", "enviaste"]', 'Condição passada contrafactual: ''tivesses enviado''.', 'Past counterfactual condition: ''tivesses enviado''.', 'Контрфактична минула умова: ''tivesses enviado''.', NULL, '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('24cab0d3-d2ec-45df-861e-4339966486b0'::uuid, 'Oxalá ___ boas notícias em breve!', 'cheguem', 'frases-exclamativas-desejo', 'B2', '["chegarão", "cheguem", "chegassem", "chegam"]', 'Com ''oxalá'', desejo provável pede presente do conjuntivo: ''cheguem''.', 'With ''oxalá'', a hopeful wish takes the present subjunctive: ''cheguem''.', 'З ''oxalá'' для побажання вживається презент кон''юнктива: ''cheguem''.', 'chegar (3ª pl.)', '0.6', '0', '2025-08-10 20:28:53.075399+01', '2025-08-10 20:28:53.075399+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6485a9ef-9428-45f5-82b2-14d78b544f16'::uuid, 'Ele afirmou: "Farei isso amanhã" → Ele afirmou que ___ isso no dia seguinte.', 'faria', 'discurso-direto-indireto', 'B1', '["fez", "fará", "faria", "faz"]', 'A resposta correta é "faria". No discurso indireto, os tempos e pronomes podem mudar: presente → pretérito imperfeito, futuro → condicional, pronomes e advérbios ajustam-se ao ponto de referência. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "faria". In reported speech, tenses and pronouns may shift: present → imperfect past, future → conditional; pronouns/adverbs adjust to the new reference point. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "faria". У непрямій мові часи та займенники можуть змінюватися: теперішній → недоконаний минулий, майбутній → умовний; займенники/прислівники узгоджуються з новою точкою відліку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'fazer', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('237827f5-f107-4678-8690-7f7d9215fdbc'::uuid, 'Não ___ tocar piano sem praticar regularmente.', 'consegues', 'poder-conseguir', 'B1', '["sabes", "podes", "consegues", "deves"]', '''Conseguir'' indica capacidade física ou mental específica.', '''Conseguir'' indicates specific physical or mental ability.', '''Conseguir'' вказує на конкретну фізичну або розумову здатність.', 'poder / conseguir', '0.5', '56', '2025-07-25 13:51:24.438984+01', '2025-08-11 17:01:26.943208+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('7768e5e4-24af-4788-a162-6c66020d8d12'::uuid, 'Não ___ fazer isso agora porque estou ocupado.', 'posso', 'poder-conseguir', 'B1', '["posso", "possa", "consigo"]', 'Usamos ''poder'' para expressar impossibilidade geral ou permissão, não capacidade física.', 'We use ''poder'' to express general impossibility or permission, not physical ability.', 'Ми використовуємо ''poder'' для вираження загальної неможливості або дозволу, а не фізичної здатності.', 'poder / conseguir', '0.5', '56', '2025-07-25 13:25:45.4803+01', '2025-08-11 17:01:27.199143+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6e85f8ab-f3c6-4453-a86c-ef4154a33e0f'::uuid, 'O relatório ___ ser entregue até amanhã.', 'tem de', 'dever-ter-de', 'B2', '["tem de", "deve", "tinha de", "devia"]', '''Ter de'' expressa uma obrigação mais forte e inevitável que ''dever''.', '''Ter de'' expresses a stronger and more unavoidable obligation than ''dever''.', '''Ter de'' виражає сильніше і неминуче зобов''язання порівняно з ''dever''.', 'dever / ter de', '0.5', '56', '2025-07-25 08:56:04.128727+01', '2025-08-11 17:01:27.826606+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('9a194bfe-215b-4f5b-b748-09d6bda16326'::uuid, 'A maior parte dos clientes ___ satisfeita.', 'está', 'concordancia-verbal', 'B1', '["estar", "está", "estão", "esteve"]', 'A resposta correta é "está". O verbo deve concordar com o sujeito em número e pessoa. Atenção a sujeitos compostos e expressões partitivas. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "está". The verb must agree with the subject in number and person. Watch for compound subjects and partitive expressions. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "está". Дієслово має узгоджуватися з підметом за числом і особою. Звертайте увагу на складені підмети та партитивні вирази. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'expressão partitiva', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('20ad9ac9-2adf-47c5-a7b7-aebb84d9fd67'::uuid, 'É possível que eles ___ cedo.', 'partam', 'presente-conjuntivo-regulares', 'B1', '["partam", "partem", "partiriam", "partiram"]', 'A resposta correta é "partam". O presente do conjuntivo usa-se após expressões de vontade, dúvida, necessidade, entre outras. Regularmente, -ar termina em -e/-es/-e/-emos/-em; -er/-ir em -a/-as/-a/-amos/-am. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "partam". Present subjunctive is used after expressions of wish, doubt, necessity, etc. Regular verbs: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "partam". Теперішній кон’юнктив уживається після виразів бажання, сумніву, необхідності тощо. Регулярні: -ar → -e/-es/-e/-emos/-em; -er/-ir → -a/-as/-a/-amos/-am. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'partir', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:36.132842+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('60e80f74-8f87-4d74-8423-41832bf703da'::uuid, 'É bom que ela ___ calma.', 'esteja', 'presente-conjuntivo-irregulares', 'B1', '["esteja", "está", "estaria", "esteve"]', 'A resposta correta é "esteja". Verbos irregulares no presente do conjuntivo: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "esteja". Irregular verbs in the present subjunctive: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "esteja". Неправильні дієслова в теперішньому кон’юнктиві: ser (seja...), estar (esteja...), ter (tenha...), ir (vá...), poder (possa...), fazer (faça...), saber (saiba...), querer (queira...). Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'estar', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:36.126446+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('124691b1-7d4f-429b-813a-d60bed95f958'::uuid, '___ mesa é grande.', 'A', 'artigos-definidos-indefinidos', 'A1', '["Um", "A", "Uma", "O"]', 'Usamos o artigo definido ''a'' com substantivos femininos singulares.', 'We use the definite article ''a'' with feminine singular nouns.', 'Використовуємо означений артикль ''a'' з іменниками жіночого роду в однині.', NULL, '0.5', '0', '2025-07-25 15:50:29.244172+01', '2025-08-10 14:54:51.917395+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('17313336-39a0-4daf-a701-2df11cda51a9'::uuid, 'Procuro uma casa ___ tenha vista para o mar.', 'que', 'oracoes-relativas-conjuntivo', 'B2', '["que", "qual", "cuja", "onde"]', 'Nas orações relativas com conjuntivo, usamos ''que'' para expressar desejo ou busca.', 'In relative clauses with subjunctive, we use ''que'' to express desire or search.', 'У відносних реченнях з кон''юнктивом використовуємо ''que'' для вираження бажання чи пошуку.', NULL, '0.5', '0', '2025-07-26 09:05:31.785946+01', '2025-08-10 12:19:39.239903+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('33aeacb0-85e0-42d1-8fef-bd6b7e6aef4c'::uuid, 'A menos que vocês ___ agora, perdemos o comboio.', 'saiam', 'conjuncoes-conjuntivo', 'B1', '["saiam", "saíram", "saem", "sairão"]', 'A resposta correta é "saiam". Muitas conjunções exigem conjuntivo: ''para que'', ''a fim de que'', ''embora'', ''antes que'', ''caso'', ''a menos que''. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "saiam". Many conjunctions trigger the subjunctive: ''para que'', ''a fim de que'', ''embora'', ''antes que'', ''caso'', ''a menos que''. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "saiam". Багато сполучників вимагають кон’юнктива: ''para que'', ''a fim de que'', ''embora'', ''antes que'', ''caso'', ''a menos que''. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'sair', '0.6', '0', '2025-08-10 20:44:52.003393+01', '2025-08-10 20:44:52.003393+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('2839ae11-a032-4c6f-ac13-7ae08bce21da'::uuid, 'Quando ___ a verdade, fiquei muito desiludido.', 'soube', 'saber-conhecer', 'B2', '["conhecia", "sabia", "conheci", "soube"]', '''Saber'' é usado para indicar o momento em que se toma conhecimento de uma informação.', '''Saber'' is used to indicate the moment of learning or finding out information.', '''Saber'' використовується для позначення моменту, коли дізнаєшся інформацію.', 'saber / conhecer', '0.5', '57', '2025-07-26 09:05:31.140808+01', '2025-08-11 17:01:46.262268+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('6bf3b619-f9ae-4d93-b867-819b28d4b945'::uuid, 'Você ___ me ajudar com as malas amanhã de manhã?', 'pode', 'poder-conseguir', 'B1', '["poderia", "pode", "conseguiria", "consegue"]', 'Neste caso, ''poder'' expressa pedido de ajuda, relacionado à permissão ou disponibilidade, não ao êxito.', 'Here, ''poder'' expresses a request for help, related to permission or availability, not success.', 'Тут ''poder'' виражає прохання про допомогу, пов''язане з дозволом чи готовністю, а не з успіхом.', 'poder / conseguir', '0.6', '57', '2025-08-10 16:07:41.890521+01', '2025-08-11 17:01:41.693151+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('8a7a37a2-5e69-46e1-9736-b91b09c5cdd6'::uuid, '___ caneta é vermelha.', 'Esta', 'demonstrativos-variaveis', 'A1', '["Aquela", "Esse", "Este", "Esta"]', '''Esta'' é usado para objetos femininos próximos de quem fala.', '''Esta'' is used for feminine objects close to the speaker.', '''Esta'' використовується для жіночих предметів, близьких до мовця.', NULL, '0.5', '0', '2025-07-25 15:50:29.287177+01', '2025-08-10 14:54:51.912051+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ce84799f-d796-4ebb-8b62-47cd9c41cd36'::uuid, 'Ele ___ fazer a manutenção do carro.', 'tem de', 'dever-ter-de', 'B1', '["tinha de", "ter", "deve", "tem de"]', 'A resposta correta é "tem de". "Dever" indica obrigação moral ou conselho; "ter de" indica necessidade objetiva. Ambos podem usar o presente para falar de obrigação no agora. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "tem de". "Dever" expresses moral obligation or advice; "ter de" expresses objective necessity. Both can be used in the present to talk about current obligation. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "tem de". "dever" виражає моральний обов’язок або пораду; "ter de" — об’єктивну необхідність. В обох випадках можливий теперішній час для поточного обов’язку. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'dever / ter de', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:36.127981+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('8a0a9205-69c3-434f-b1aa-eac54a0bdb13'::uuid, '___ é a tua caneta?', 'Onde', 'interrogativos', 'A1', '["Quando", "Onde", "Como"]', '''Onde'' é usado para perguntar sobre localização.', '''Onde'' (where) is used to ask about location.', '''Onde'' (де) використовується для запитання про місцезнаходження.', NULL, '0.5', '0', '2025-07-25 15:50:27.213171+01', '2025-08-10 12:45:45.200025+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('ccccfa12-58e0-4299-889c-116da40b7669'::uuid, '___ alunos fizeram o exame hoje.', 'Alguns', 'pronomes-indefinidos', 'B1', '["Alguns", "Qualquer", "Todo", "Cada"]', 'Usamos ''alguns'' para indicar uma quantidade indefinida no plural.', 'We use ''alguns'' (some) to indicate an indefinite plural quantity.', 'Ми використовуємо ''alguns'' (деякі) для позначення невизначеної кількості у множині.', NULL, '0.5', '0', '2025-07-25 15:59:25.223335+01', '2025-08-10 12:19:43.605121+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('c3c3ee8b-5e63-4cc1-af9e-624e2e5af7a6'::uuid, '___ menino está a brincar.', 'O', 'artigos-definidos-indefinidos', 'A1', '["O", "Os", "Um", "A"]', 'Usamos o artigo definido ''o'' para um substantivo masculino singular específico.', 'We use the definite article ''o'' for a specific singular masculine noun.', 'Ми використовуємо означений артикль ''o'' для конкретного іменника чоловічого роду в однині.', NULL, '0.5', '0', '2025-07-25 13:22:48.088253+01', '2025-08-10 12:44:28.297014+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('b9c87261-534f-4149-9364-0d8528e72e3f'::uuid, 'Depois de meses de treino, ela finalmente ___ correr 10 km sem parar.', 'conseguiu', 'poder-conseguir', 'B1', '["conseguiu", "pôde", "podemos", "conseguia"]', '''Conseguir'' enfatiza que ela alcançou o objetivo de correr 10 km sem parar, algo conquistado com esforço.', '''Conseguir'' emphasizes that she achieved the goal of running 10 km without stopping, something accomplished with effort.', '''Conseguir'' підкреслює, що вона досягла мети пробігти 10 км без зупинки, чого вона досягла завдяки зусиллям.', 'poder / conseguir', '0.6', '57', '2025-08-10 16:07:41.891548+01', '2025-08-11 17:01:41.692552+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('e8933edb-b530-49da-a51e-d94dd9a491c5'::uuid, '___ livro está na mesa?', 'Que', 'interrogativos', 'A1', '["Que", "Qual", "Quem", "Onde"]', '''Que'' é usado para fazer perguntas sobre a identidade de objetos.', '''Que'' is used to ask questions about the identity of objects.', '''Que'' використовується для запитань про ідентифікацію предметів.', NULL, '0.5', '0', '2025-07-25 15:50:29.288046+01', '2025-08-10 14:54:51.916272+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('faf83bb9-9658-4e4c-9044-43040a6d5f00'::uuid, 'A declaração ___ ser entregue até amanhã.', 'tem de', 'dever-ter-de', 'B1', '["tem de", "deve", "há de", "precise de"]', '''Ter de'' expressa uma obrigação absoluta, mais forte que ''dever'', que sugere recomendação.', '''Ter de'' expresses absolute obligation, stronger than ''dever'', which suggests recommendation.', '''Ter de'' виражає абсолютне зобов''язання, сильніше ніж ''dever'', який передає рекомендацію.', 'dever / ter de', '0.5', '57', '2025-07-26 09:05:31.229869+01', '2025-08-11 17:01:44.371866+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('66376e9c-475a-40a3-91dd-02185ec327ed'::uuid, 'Duvido que ela ___ a verdade sobre o acontecimento.', 'saiba', 'saber-conhecer', 'B2', '["saiba", "conheça", "conhece", "sabe"]', '''Saber'' é usado para conhecimento de informações específicas, e ''duvidar'' exige o conjuntivo.', '''Saber'' is used for knowledge of specific information, and ''duvidar'' requires the subjunctive.', '''Saber'' використовується для знання конкретної інформації, а після ''duvidar'' вживається суб''юнктив.', 'saber / conhecer', '0.5', '57', '2025-07-26 09:05:31.229262+01', '2025-08-11 17:01:46.260421+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('97982067-2ae2-45b2-a7c2-3ade88919b7e'::uuid, 'Se você estudar mais, ___ passar no exame.', 'poderá', 'poder-conseguir', 'B1', '["poderá", "consegue", "pode", "conseguirá"]', '''Poder'' indica possibilidade futura. Se estudar mais, haverá possibilidade de passar.', '''Poder'' indicates future possibility. If you study more, there will be a chance to pass.', '''Poder'' вказує на можливість у майбутньому. Якщо ти більше вчитимешся, буде шанс скласти іспит.', 'poder / conseguir', '0.6', '57', '2025-08-10 16:07:41.893897+01', '2025-08-11 16:59:42.820772+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('2909fbbc-6aa7-4823-8d1c-8ac542606731'::uuid, 'Não ___ falar com ela por causa do barulho.', 'consegui', 'poder-conseguir', 'B1', '["podia", "pude", "consigo", "consegui"]', '''Conseguir'' indica capacidade ou êxito numa tentativa específica.', '''Conseguir'' indicates ability or success in a specific attempt.', '''Conseguir'' вказує на здатність або успіх у конкретній спробі.', 'poder / conseguir', '0.5', '57', '2025-07-26 09:07:03.290933+01', '2025-08-11 17:01:42.900983+01');
INSERT INTO public.exercises (id, sentence, correct_answer, topic, level, multiple_choice_options, explanation_pt, explanation_en, explanation_uk, hint, difficulty_score, usage_count, created_at, updated_at) VALUES ('da04ac67-3ffe-47cb-a734-ecf411bd5166'::uuid, 'A música ___ por um coro infantil.', 'é cantada', 'voz-passiva', 'B1', '["são cantadas", "é cantada", "é cantado", "foi cantada"]', 'A resposta correta é "é cantada". A voz passiva analítica forma-se com "ser" + particípio passado, concordando em género e número com o sujeito paciente. A frase exige esta forma para manter a correção gramatical e o sentido pretendido.', 'The correct answer is "é cantada". Analytical passive voice is formed with "ser" + past participle, which agrees in gender and number with the patient subject. This form fits the context and keeps the sentence grammatically correct.', 'Правильна відповідь — "é cantada". Аналітичний пасив утворюється за допомогою "ser" + дієприкметник минулого, який узгоджується за родом і числом із підметом. Ця форма відповідає контексту і зберігає граматичну правильність речення.', 'cantar', '0.6', '57', '2025-08-10 20:44:52.003393+01', '2025-08-11 17:01:36.130012+01');
